        long current = ((Nd4jWorkspace) map.get(key)).getCurrentSize();
        long spilled = ((Nd4jWorkspace) map.get(key)).getSpilledSize();
        long pinned = ((Nd4jWorkspace) map.get(key)).getPinnedSize();
        log.info(String.format("%-26s %8s / %8s / %8s (d / d / d)", (key + ":"), StringUtils.TraditionalBinaryPrefix.long2String(current, "", 2), StringUtils.TraditionalBinaryPrefix.long2String(spilled, "", 2), StringUtils.TraditionalBinaryPrefix.long2String(pinned, "", 2), current, spilled, pinned));
    }
}

