void copyAtStride(int n,BufferType bufferType,ByteBuffer from,int fromOffset,int fromStride,ByteBuffer to,int toOffset,int toStride){
    ByteBuffer fromView = from;
    ByteBuffer toView = to;
    fromView.order(ByteOrder.nativeOrder());
    toView.order(ByteOrder.nativeOrder());
    switch(bufferType) {
        case INT:
            IntBuffer fromInt = fromView.asIntBuffer();
            IntBuffer toInt = toView.asIntBuffer();
            for (int i = 0; i < n; i++) {
                int put = fromInt.get(fromOffset + i * fromStride);
                toInt.put(toOffset + i * toStride, put);
            }
            break;
        case FLOAT:
            FloatBuffer fromFloat = fromView.asFloatBuffer();
            FloatBuffer toFloat = toView.asFloatBuffer();
            for (int i = 0; i < n; i++) {
                float put = fromFloat.get(fromOffset + i * fromStride);
                toFloat.put(toOffset + i * toStride, put);
            }
            break;
        case DOUBLE:
            DoubleBuffer fromDouble = fromView.asDoubleBuffer();
            DoubleBuffer toDouble = toView.asDoubleBuffer();
            for (int i = 0; i < n; i++) {
                toDouble.put(toOffset + i * toStride, fromDouble.get(fromOffset + i * fromStride));
            }
            break;
        default:
            throw new IllegalArgumentException("Only floats and double supported");
    }
}

INDArray tensorMmul(INDArray a,INDArray b,INDArray result,int[][] axes,INDArray tensorMmul,INDArray a,INDArray b,int[][] axes){
    int validationLength = Math.min(axes[0].length, axes[1].length);
    for (int i = 0; i < validationLength; i++) {
        if (a.size(axes[0][i]) != b.size(axes[1][i]))
            throw new IllegalArgumentException("Size of the given axes at each dimension must be the same size.");
        if (axes[0][i] < 0)
            axes[0][i] += a.rank();
        if (axes[1][i] < 0)
            axes[1][i] += b.rank();
    }
    List<Integer> listA = new ArrayList();
    for (int i = 0; i < a.rank(); i++) {
        if (!Ints.contains(axes[0], i))
            listA.add(i);
    }
    int[] newAxesA = Ints.concat(Ints.toArray(listA), axes[0]);
    List<Integer> listB = new ArrayList();
    for (int i = 0; i < b.rank(); i++) {
        if (!Ints.contains(axes[1], i))
            listB.add(i);
    }
    int[] newAxesB = Ints.concat(axes[1], Ints.toArray(listB));
    int n2 = 1;
    int aLength = Math.min(a.rank(), axes[0].length);
    for (int i = 0; i < aLength; i++) {
        n2 *= a.size(axes[0][i]);
    }
    long[] newShapeA = { -1, n2 };
    long[] oldShapeA;
    if (listA.size() == 0) {
        oldShapeA = new long[] { 1 };
    } else {
        oldShapeA = Longs.toArray(listA);
        for (int i = 0; i < oldShapeA.length; i++) oldShapeA[i] = a.size((int) oldShapeA[i]);
    }
    int n3 = 1;
    int bNax = Math.min(b.rank(), axes[1].length);
    for (int i = 0; i < bNax; i++) {
        n3 *= b.size(axes[1][i]);
    }
    long[] newShapeB = { n3, -1 };
    long[] oldShapeB;
    if (listB.size() == 0) {
        oldShapeB = new long[] { 1 };
    } else {
        oldShapeB = Longs.toArray(listB);
        for (int i = 0; i < oldShapeB.length; i++) oldShapeB[i] = b.size((int) oldShapeB[i]);
    }
    INDArray at = a.permute(newAxesA).reshape(newShapeA);
    INDArray bt = b.permute(newAxesB).reshape(newShapeB);
    INDArray ret = at.mmul(bt);
    long[] aPlusB = Longs.concat(oldShapeA, oldShapeB);
    return ret.reshape(aPlusB);
}

