void testLossFunctionScoreSizeMismatchCase(ILossFunction loss){
    try {
        INDArray labels = Nd4j.create(100, 32);
        INDArray preOutput = Nd4j.create(100, 44);
        double score = loss.computeScore(labels, preOutput, Activation.IDENTITY.getActivationFunction(), null, true);
        Assert.assertFalse("Loss function " + loss.toString() + "did not check for size mismatch.  This should fail to compute an activation function because the sizes of the vectors are not equal", true);
    } catch (IllegalArgumentException ex) {
        String exceptionMessage = ex.getMessage();
        Assert.assertTrue("Loss function exception " + loss.toString() + " did not indicate size mismatch when vectors of incorrect size were used.", exceptionMessage.contains("shapes"));
    }
    try {
