Optional<Resp> performRequestAndParseOptionalEntity(Req request,CheckedFunction<Req, Request, IOException> requestConverter,RequestOptions options,CheckedFunction<XContentParser, Resp, IOException> entityParser){
    Optional<ValidationException> validationException = request.validate();
    if (validationException != null && validationException.isPresent()) {
        throw validationException.get();
    }
    Request req = requestConverter.apply(request);
    req.setOptions(options);
    Response response;
    try {
        response = client.performRequest(req);
    } catch (ResponseException e) {
        if (RestStatus.NOT_FOUND.getStatus() == e.getResponse().getStatusLine().getStatusCode()) {
            return Optional.empty();
        }
        throw parseResponseException(e);
    }
    try {
        return Optional.of(parseEntity(response.getEntity(), entityParser));
    } catch (Exception e) {
        throw new IOException("Unable to parse response body for " + response, e);
    }
}

