EvalQueryQuality evaluate(String taskId,SearchHit[] hits,List<RatedDocument> ratedDocs){
    List<RatedSearchHit> ratedHits = joinHitsWithRatings(hits, ratedDocs);
    int firstRelevant = -1;
    int rank = 1;
    for (RatedSearchHit hit : ratedHits) {
        OptionalInt rating = hit.getRating();
        if (rating.isPresent()) {
            if (rating.getAsInt() >= this.relevantRatingThreshhold) {
                firstRelevant = rank;
                break;
            }
        }
        rank++;
    }
    double reciprocalRank = (firstRelevant == -1) ? 0 : 1.0d / firstRelevant;
    EvalQueryQuality evalQueryQuality = new EvalQueryQuality(taskId, reciprocalRank);
    evalQueryQuality.setMetricDetails(new Detail(firstRelevant));
    evalQueryQuality.addHitsAndRatings(ratedHits);
    return evalQueryQuality;
}

MarkupPassage mergeAnnotations(AnnotationToken[] annotations,Passage passage){
    try {
        MarkupPassage markupPassage = new MarkupPassage();
        for (int i = 0; i < passage.getNumMatches(); i++) {
            int start = passage.getMatchStarts()[i];
            int end = passage.getMatchEnds()[i];
            String searchTerm = passage.getMatchTerms()[i].utf8ToString();
            Markup markup = new Markup(start, end, SEARCH_HIT_TYPE + "=" + URLEncoder.encode(searchTerm, StandardCharsets.UTF_8.name()));
            markupPassage.addUnlessOverlapping(markup);
        }
        for (AnnotationToken token : annotations) {
            int start = token.offset;
            int end = token.endOffset;
            if (start >= passage.getStartOffset() && end <= passage.getEndOffset()) {
                String escapedValue = URLEncoder.encode(token.value, StandardCharsets.UTF_8.name());
                Markup markup = new Markup(start, end, escapedValue);
                markupPassage.addUnlessOverlapping(markup);
            }
        }
        return markupPassage;
    } catch (UnsupportedEncodingException e) {
        throw new IllegalStateException(e);
    }
}

