INDArray pullRows(INDArray source,int sourceDimension,int[] indexes,INDArray pullRows,INDArray source,int sourceDimension,long[] indexes,INDArray pullRows,INDArray source,int sourceDimension,long[] indexes,char order,INDArray pullRows,INDArray source,int sourceDimension,int[] indexes,char order,INDArray pullRows,INDArray source,INDArray destination,int sourceDimension,int[] indexes,INDArray pullRows,INDArray source,INDArray destination,int sourceDimension,long[] indexes){
    if (indexes == null || indexes.length < 1)
        throw new IllegalStateException("Indexes can't be null or zero-length");
    long[] shape = null;
    if (sourceDimension == 1)
        shape = new long[] { indexes.length, source.shape()[sourceDimension] };
    else if (sourceDimension == 0)
        shape = new long[] { source.shape()[sourceDimension], indexes.length };
    else
        throw new UnsupportedOperationException("2D input is expected");
    INDArray ret = destination;
    if (ret == null) {
        ret = Nd4j.createUninitialized(shape, order);
    } else {
        if (!Arrays.equals(shape, destination.shape())) {
            throw new IllegalStateException("Cannot pull rows into destination array: expected destination array of" + " shape " + Arrays.toString(shape) + " but got destination array of shape " + Arrays.toString(destination.shape()));
        }
    }
    Nd4j.getCompressor().autoDecompress(source);
    PointerPointer dummy = new PointerPointer(new Pointer[] { null });
    TADManager tadManager = Nd4j.getExecutioner().getTADManager();
    Pair<DataBuffer, DataBuffer> tadBuffers = tadManager.getTADOnlyShapeInfo(source, new int[] { sourceDimension });
    Pair<DataBuffer, DataBuffer> zTadBuffers = tadManager.getTADOnlyShapeInfo(ret, new int[] { sourceDimension });
    Pointer hostTadShapeInfo = tadBuffers.getFirst().addressPointer();
    Pointer zTadShapeInfo = zTadBuffers.getFirst().addressPointer();
    LongPointer pIndex = new LongPointer(indexes);
    DataBuffer offsets = tadBuffers.getSecond();
    Pointer hostTadOffsets = offsets == null ? null : offsets.addressPointer();
    DataBuffer zOffsets = zTadBuffers.getSecond();
    Pointer zTadOffsets = zOffsets == null ? null : zOffsets.addressPointer();
    if (ret.data().dataType() == DataBuffer.Type.DOUBLE) {
        nativeOps.pullRowsDouble(dummy, (DoublePointer) source.data().addressPointer(), (LongPointer) source.shapeInfoDataBuffer().addressPointer(), (DoublePointer) ret.data().addressPointer(), (LongPointer) ret.shapeInfoDataBuffer().addressPointer(), indexes.length, pIndex, (LongPointer) hostTadShapeInfo, new LongPointerWrapper(hostTadOffsets), (LongPointer) zTadShapeInfo, new LongPointerWrapper(zTadOffsets));
    } else if (ret.data().dataType() == DataBuffer.Type.FLOAT) {
        nativeOps.pullRowsFloat(dummy, (FloatPointer) source.data().addressPointer(), (LongPointer) source.shapeInfoDataBuffer().addressPointer(), (FloatPointer) ret.data().addressPointer(), (LongPointer) ret.shapeInfoDataBuffer().addressPointer(), indexes.length, pIndex, (LongPointer) hostTadShapeInfo, new LongPointerWrapper(hostTadOffsets), (LongPointer) zTadShapeInfo, new LongPointerWrapper(zTadOffsets));
    } else {
        nativeOps.pullRowsHalf(dummy, (ShortPointer) source.data().addressPointer(), (LongPointer) source.shapeInfoDataBuffer().addressPointer(), (ShortPointer) ret.data().addressPointer(), (LongPointer) ret.shapeInfoDataBuffer().addressPointer(), indexes.length, pIndex, (LongPointer) hostTadShapeInfo, new LongPointerWrapper(hostTadOffsets), (LongPointer) zTadShapeInfo, new LongPointerWrapper(zTadOffsets));
    }
    return ret;
}

