double getValFromUpperTriangularMatrix(Map<String, M> map,String fieldX,String fieldY){
    if (map.containsKey(fieldX) == false && map.containsKey(fieldY) == false) {
        throw new IllegalArgumentException("neither field " + fieldX + " nor " + fieldY + " exist");
    } else if (map.containsKey(fieldX)) {
        if (map.get(fieldX).containsKey(fieldY)) {
            return map.get(fieldX).get(fieldY);
        } else {
            return map.get(fieldY).get(fieldX);
        }
    } else if (map.containsKey(fieldY)) {
        return map.get(fieldY).get(fieldX);
    }
    throw new IllegalArgumentException("Coefficient not computed between fields: " + fieldX + " and " + fieldY);
}

XContentBuilder toXContentGroupedByNode(XContentBuilder builder,Params params,DiscoveryNodes discoveryNodes){
    toXContentCommon(builder, params);
    builder.startObject("nodes");
    for (Map.Entry<String, List<TaskInfo>> entry : getPerNodeTasks().entrySet()) {
        DiscoveryNode node = discoveryNodes.get(entry.getKey());
        builder.startObject(entry.getKey());
        if (node != null) {
            builder.field("name", node.getName());
            builder.field("transport_address", node.getAddress().toString());
            builder.field("host", node.getHostName());
            builder.field("ip", node.getAddress());
            builder.startArray("roles");
            for (DiscoveryNode.Role role : node.getRoles()) {
                builder.value(role.getRoleName());
            }
            builder.endArray();
            if (!node.getAttributes().isEmpty()) {
                builder.startObject("attributes");
                for (Map.Entry<String, String> attrEntry : node.getAttributes().entrySet()) {
                    builder.field(attrEntry.getKey(), attrEntry.getValue());
                }
                builder.endObject();
            }
        }
        builder.startObject(TASKS);
        for (TaskInfo task : entry.getValue()) {
            builder.startObject(task.getTaskId().toString());
            task.toXContent(builder, params);
            builder.endObject();
        }
        builder.endObject();
        builder.endObject();
    }
    builder.endObject();
    return builder;
}

