Collection<VoidChunk> split(VoidMessage message,int maxBytes){
    if (maxBytes <= 0)
        throw new ND4JIllegalStateException("MaxBytes must be > 0");
    val tempFile = ND4JFileUtils.createTempFile("messageSplitter", "temp");
    val result = new ArrayList<VoidChunk>();
    try (val fos = new FileOutputStream(tempFile);
        val bos = new BufferedOutputStream(fos)) {
        SerializationUtils.serialize(message, fos);
        val length = tempFile.length();
        int numChunks = (int) (length / maxBytes + (length % maxBytes > 0 ? 1 : 0));
        try (val fis = new FileInputStream(tempFile);
            val bis = new BufferedInputStream(fis)) {
            val bytes = new byte[maxBytes];
            int cnt = 0;
            int id = 0;
            while (cnt < length) {
                val c = bis.read(bytes);
                val tmp = Arrays.copyOf(bytes, c);
                val msg = VoidChunk.builder().messageId(java.util.UUID.randomUUID().toString()).originalId(message.getMessageId()).chunkId(id++).numberOfChunks(numChunks).splitSize(maxBytes).payload(tmp).totalSize(length).build();
                result.add(msg);
                cnt += c;
            }
        }
    }
    tempFile.delete();
    return result;
}

