int searchAfterDoc(TopComparator comparator,int from,int to){
    int low = from;
    int high = to - 1;
    while (low <= high) {
        int mid = (low + high) >>> 1;
        if (comparator.lessThanTop(mid)) {
            high = mid - 1;
        } else {
            low = mid + 1;
        }
    }
    return low;
}

