    SearchResponse response = client().prepareSearch("agg_index").addAggregation(histogram("histogram").field("one").interval(2).subAggregation(sum("twoSum").field("two")).subAggregation(sum("threeSum").field("three")).subAggregation(sum("fourSum").field("four")).subAggregation(bucketScript("totalSum", new Script(ScriptType.INLINE, ExpressionScriptEngine.NAME, "_value0 + _value1 + _value2", Collections.emptyMap()), "twoSum", "threeSum", "fourSum"))).execute().actionGet();
    Histogram histogram = response.getAggregations().get("histogram");
    assertThat(histogram, notNullValue());
    assertThat(histogram.getName(), equalTo("histogram"));
    List<? extends Histogram.Bucket> buckets = histogram.getBuckets();
    for (int bucketCount = 0; bucketCount < buckets.size(); ++bucketCount) {
        Histogram.Bucket bucket = buckets.get(bucketCount);
        if (bucket.getDocCount() == 1) {
            SimpleValue seriesArithmetic = bucket.getAggregations().get("totalSum");
            assertThat(seriesArithmetic, notNullValue());
            double seriesArithmeticValue = seriesArithmetic.value();
            assertEquals(9.0, seriesArithmeticValue, 0.001);
        } else if (bucket.getDocCount() == 2) {
            SimpleValue seriesArithmetic = bucket.getAggregations().get("totalSum");
            assertThat(seriesArithmetic, notNullValue());
            double seriesArithmeticValue = seriesArithmetic.value();
            assertEquals(18.0, seriesArithmeticValue, 0.001);
        } else {
            fail("Incorrect number of documents in a bucket in the histogram.");
        }
    }
}

String formatTime(long timeDiff){
    StringBuilder buf = new StringBuilder();
    long hours = timeDiff / (60 * 60 * 1000);
    long rem = (timeDiff % (60 * 60 * 1000));
    long minutes = rem / (60 * 1000);
    rem = rem % (60 * 1000);
    long seconds = rem / 1000;
    if (hours != 0) {
        buf.append(hours);
        buf.append("hrs, ");
    }
    if (minutes != 0) {
        buf.append(minutes);
        buf.append("mins, ");
    }
    buf.append(seconds);
    buf.append("sec");
    return buf.toString();
}

