double bernoullis(double n,double k,double successProb){
    double combo = MathUtils.combination(n, k);
    double p = successProb;
    double q = 1 - successProb;
    return combo * Math.pow(p, k) * Math.pow(q, n - k);
}

void copyFiles(Manifest manifest,JarFile in,JarOutputStream out,long timestamp){
    byte[] buffer = new byte[4096];
    int num;
    Map<String, Attributes> entries = manifest.getEntries();
    List<String> names = new ArrayList(entries.keySet());
    Collections.sort(names);
    for (String name : names) {
        JarEntry inEntry = in.getJarEntry(name);
        JarEntry outEntry = null;
        if (inEntry.getMethod() == JarEntry.STORED) {
            outEntry = new JarEntry(inEntry);
        } else {
            outEntry = new JarEntry(name);
        }
        outEntry.setTime(timestamp);
        out.putNextEntry(outEntry);
        InputStream data = in.getInputStream(inEntry);
        while ((num = data.read(buffer)) > 0) {
            out.write(buffer, 0, num);
        }
        out.flush();
    }
}

