void validateIncomingState(Logger logger,ClusterState incomingState,ClusterState lastState){
    final ClusterName incomingClusterName = incomingState.getClusterName();
    if (!incomingClusterName.equals(lastState.getClusterName())) {
        logger.warn("received cluster state from [{}] which is also master but with a different cluster name [{}]", incomingState.nodes().getMasterNode(), incomingClusterName);
        throw new IllegalStateException("received state from a node that is not part of the cluster");
    }
    if (lastState.nodes().getLocalNode().equals(incomingState.nodes().getLocalNode()) == false) {
        logger.warn("received a cluster state from [{}] and not part of the cluster, should not happen", incomingState.nodes().getMasterNode());
        throw new IllegalStateException("received state with a local node that does not match the current local node");
    }
    if (shouldIgnoreOrRejectNewClusterState(logger, lastState, incomingState)) {
        String message = String.format(Locale.ROOT, "rejecting cluster state version [%d] uuid [%s] received from [%s]", incomingState.version(), incomingState.stateUUID(), incomingState.nodes().getMasterNodeId());
        logger.warn(message);
        throw new IllegalStateException(message);
    }
}

ShardIterator onlyNodeSelectorActiveInitializingShardsIt(String nodeAttributes,DiscoveryNodes discoveryNodes,ShardIterator onlyNodeSelectorActiveInitializingShardsIt,String[] nodeAttributes,DiscoveryNodes discoveryNodes){
    ArrayList<ShardRouting> ordered = new ArrayList(activeShards.size() + allInitializingShards.size());
    Set<String> selectedNodes = Sets.newHashSet(discoveryNodes.resolveNodes(nodeAttributes));
    int seed = shuffler.nextSeed();
    for (ShardRouting shardRouting : shuffler.shuffle(activeShards, seed)) {
        if (selectedNodes.contains(shardRouting.currentNodeId())) {
            ordered.add(shardRouting);
        }
    }
    for (ShardRouting shardRouting : shuffler.shuffle(allInitializingShards, seed)) {
        if (selectedNodes.contains(shardRouting.currentNodeId())) {
            ordered.add(shardRouting);
        }
    }
    if (ordered.isEmpty()) {
        final String message = String.format(Locale.ROOT, "no data nodes with %s [%s] found for shard: %s", nodeAttributes.length == 1 ? "criteria" : "criterion", String.join(",", nodeAttributes), shardId());
        throw new IllegalArgumentException(message);
    }
    return new PlainShardIterator(shardId, ordered);
}

