Collection<String> getCliqueFeatures(PaddedList<IN> cInfo,int loc,Clique clique){
    Collection<String> features = Generics.newHashSet();
    if (clique == cliqueC) {
        addAllInterningAndSuffixing(features, featuresC(cInfo, loc), "C");
    } else if (clique == cliqueCpC) {
        addAllInterningAndSuffixing(features, featuresCpC(cInfo, loc), "CpC");
    } else if (clique == cliqueCp2C) {
        addAllInterningAndSuffixing(features, featuresCp2C(cInfo, loc), "Cp2C");
    } else if (clique == cliqueCp3C) {
        addAllInterningAndSuffixing(features, featuresCp3C(cInfo, loc), "Cp3C");
    }
    String domain = cInfo.get(loc).get(CoreAnnotations.DomainAnnotation.class);
    if (domain != null) {
        Collection<String> domainFeatures = Generics.newHashSet();
        for (String feature : features) {
            domainFeatures.add(feature + DOMAIN_MARKER + domain);
        }
        features.addAll(domainFeatures);
    }
    return features;
}

void assertCanWrite(){
    for (Path path : nodeDataPaths()) {
        tryWriteTempFile(path);
    }
    for (String indexFolderName : this.availableIndexFolders()) {
        for (Path indexPath : this.resolveIndexFolder(indexFolderName)) {
            Path indexStatePath = indexPath.resolve(MetaDataStateFormat.STATE_DIR_NAME);
            tryWriteTempFile(indexStatePath);
            tryWriteTempFile(indexPath);
            try (DirectoryStream<Path> stream = Files.newDirectoryStream(indexPath)) {
                for (Path shardPath : stream) {
                    String fileName = shardPath.getFileName().toString();
                    if (Files.isDirectory(shardPath) && fileName.chars().allMatch(Character::isDigit)) {
                        Path indexDir = shardPath.resolve(ShardPath.INDEX_FOLDER_NAME);
                        Path statePath = shardPath.resolve(MetaDataStateFormat.STATE_DIR_NAME);
                        Path translogDir = shardPath.resolve(ShardPath.TRANSLOG_FOLDER_NAME);
                        tryWriteTempFile(indexDir);
