void warnAboutDiskIfNeeded(DiskUsage usage){
    if (usage.getFreeBytes() < diskThresholdSettings.getFreeBytesThresholdFloodStage().getBytes()) {
        logger.warn("flood stage disk watermark [{}] exceeded on {}, all indices on this node will be marked read-only", diskThresholdSettings.getFreeBytesThresholdFloodStage(), usage);
    } else if (usage.getFreeBytes() < diskThresholdSettings.getFreeBytesThresholdHigh().getBytes()) {
        logger.warn("high disk watermark [{}] exceeded on {}, shards will be relocated away from this node", diskThresholdSettings.getFreeBytesThresholdHigh(), usage);
    } else if (usage.getFreeBytes() < diskThresholdSettings.getFreeBytesThresholdLow().getBytes()) {
        logger.info("low disk watermark [{}] exceeded on {}, replicas will not be assigned to this node", diskThresholdSettings.getFreeBytesThresholdLow(), usage);
    }
    if (usage.getFreeDiskAsPercentage() < diskThresholdSettings.getFreeDiskThresholdFloodStage()) {
        logger.warn("flood stage disk watermark [{}] exceeded on {}, all indices on this node will be marked read-only", Strings.format1Decimals(100.0 - diskThresholdSettings.getFreeDiskThresholdFloodStage(), "%"), usage);
    } else if (usage.getFreeDiskAsPercentage() < diskThresholdSettings.getFreeDiskThresholdHigh()) {
        logger.warn("high disk watermark [{}] exceeded on {}, shards will be relocated away from this node", Strings.format1Decimals(100.0 - diskThresholdSettings.getFreeDiskThresholdHigh(), "%"), usage);
    } else if (usage.getFreeDiskAsPercentage() < diskThresholdSettings.getFreeDiskThresholdLow()) {
        logger.info("low disk watermark [{}] exceeded on {}, replicas will not be assigned to this node", Strings.format1Decimals(100.0 - diskThresholdSettings.getFreeDiskThresholdLow(), "%"), usage);
    }
}

void addSortedBundle(Bundle bundle,Map<String, Bundle> bundles,LinkedHashSet<Bundle> sortedBundles,LinkedHashSet<String> dependencyStack){
    String name = bundle.plugin.getName();
    if (dependencyStack.contains(name)) {
        StringBuilder msg = new StringBuilder("Cycle found in plugin dependencies: ");
        dependencyStack.forEach(s -> {
            msg.append(s);
            msg.append(" -> ");
        });
        msg.append(name);
        throw new IllegalStateException(msg.toString());
    }
    if (sortedBundles.contains(bundle)) {
        return;
    }
    dependencyStack.add(name);
    for (String dependency : bundle.plugin.getExtendedPlugins()) {
        Bundle depBundle = bundles.get(dependency);
        if (depBundle == null) {
            throw new IllegalArgumentException("Missing plugin [" + dependency + "], dependency of [" + name + "]");
        }
        addSortedBundle(depBundle, bundles, sortedBundles, dependencyStack);
        assert sortedBundles.contains(depBundle);
    }
    dependencyStack.remove(name);
    sortedBundles.add(bundle);
}

