TableFactor marginalizeMessage(TableFactor message,int[] relevant,MarginalizationMethod marginalize){
    TableFactor result = message;
    for (int i : message.neighborIndices) {
        boolean contains = false;
        for (int j : relevant) {
            if (i == j) {
                contains = true;
                break;
            }
        }
        if (!contains) {
            switch(marginalize) {
                case SUM:
                    result = result.sumOut(i);
                    break;
                case MAX:
                    result = result.maxOut(i);
                    break;
            }
        }
    }
    return result;
}

