    while (true) {
        int nextMarker = findNextMarker(marker, from, data, length);
        if (nextMarker == -1) {
            break;
        }
        line++;
        try (InputStream stream = data.slice(from, nextMarker - from).streamInput();
            XContentParser parser = xContent.createParser(NamedXContentRegistry.EMPTY, LoggingDeprecationHandler.INSTANCE, stream)) {
            from = nextMarker + 1;
            XContentParser.Token token = parser.nextToken();
            if (token == null) {
                continue;
            }
            if (token != XContentParser.Token.START_OBJECT) {
                throw new IllegalArgumentException("Malformed action/metadata line [" + line + "], expected " + XContentParser.Token.START_OBJECT + " but found [" + token + "]");
            }
            token = parser.nextToken();
            if (token != XContentParser.Token.FIELD_NAME) {
                throw new IllegalArgumentException("Malformed action/metadata line [" + line + "], expected " + XContentParser.Token.FIELD_NAME + " but found [" + token + "]");
            }
            String action = parser.currentName();
            String index = defaultIndex;
            String type = defaultType;
            String id = null;
            String routing = valueOrDefault(defaultRouting, globalRouting);
            FetchSourceContext fetchSourceContext = defaultFetchSourceContext;
            String opType = null;
            long version = Versions.MATCH_ANY;
            VersionType versionType = VersionType.INTERNAL;
            int retryOnConflict = 0;
            String pipeline = valueOrDefault(defaultPipeline, globalPipeline);
            token = parser.nextToken();
            if (token == XContentParser.Token.START_OBJECT) {
                String currentFieldName = null;
                while ((token = parser.nextToken()) != XContentParser.Token.END_OBJECT) {
                    if (token == XContentParser.Token.FIELD_NAME) {
                        currentFieldName = parser.currentName();
                    } else if (token.isValue()) {
                        if (INDEX.match(currentFieldName, parser.getDeprecationHandler())) {
                            if (!allowExplicitIndex) {
                                throw new IllegalArgumentException("explicit index in bulk is not allowed");
                            }
                            index = parser.text();
                        } else if (TYPE.match(currentFieldName, parser.getDeprecationHandler())) {
                            type = parser.text();
                        } else if (ID.match(currentFieldName, parser.getDeprecationHandler())) {
                            id = parser.text();
                        } else if (ROUTING.match(currentFieldName, parser.getDeprecationHandler())) {
                            routing = parser.text();
                        } else if (OP_TYPE.match(currentFieldName, parser.getDeprecationHandler())) {
                            opType = parser.text();
                        } else if (VERSION.match(currentFieldName, parser.getDeprecationHandler())) {
                            version = parser.longValue();
                        } else if (VERSION_TYPE.match(currentFieldName, parser.getDeprecationHandler())) {
                            versionType = VersionType.fromString(parser.text());
                        } else if (RETRY_ON_CONFLICT.match(currentFieldName, parser.getDeprecationHandler())) {
                            retryOnConflict = parser.intValue();
                        } else if (PIPELINE.match(currentFieldName, parser.getDeprecationHandler())) {
                            pipeline = parser.text();
                        } else if (SOURCE.match(currentFieldName, parser.getDeprecationHandler())) {
                            fetchSourceContext = FetchSourceContext.fromXContent(parser);
                        } else {
                            throw new IllegalArgumentException("Action/metadata line [" + line + "] contains an unknown parameter [" + currentFieldName + "]");
                        }
                    } else if (token == XContentParser.Token.START_ARRAY) {
                        throw new IllegalArgumentException("Malformed action/metadata line [" + line + "], expected a simple value for field [" + currentFieldName + "] but found [" + token + "]");
                    } else if (token == XContentParser.Token.START_OBJECT && SOURCE.match(currentFieldName, parser.getDeprecationHandler())) {
                        fetchSourceContext = FetchSourceContext.fromXContent(parser);
                    } else if (token != XContentParser.Token.VALUE_NULL) {
                        throw new IllegalArgumentException("Malformed action/metadata line [" + line + "], expected a simple value for field [" + currentFieldName + "] but found [" + token + "]");
                    }
                }
            } else if (token != XContentParser.Token.END_OBJECT) {
                throw new IllegalArgumentException("Malformed action/metadata line [" + line + "], expected " + XContentParser.Token.START_OBJECT + " or " + XContentParser.Token.END_OBJECT + " but found [" + token + "]");
            }
            if ("delete".equals(action)) {
                add(new DeleteRequest(index, type, id).routing(routing).version(version).versionType(versionType), payload);
            } else {
                nextMarker = findNextMarker(marker, from, data, length);
                if (nextMarker == -1) {
                    break;
                }
                line++;
                if ("index".equals(action)) {
                    if (opType == null) {
                        internalAdd(new IndexRequest(index, type, id).routing(routing).version(version).versionType(versionType).setPipeline(pipeline).source(sliceTrimmingCarriageReturn(data, from, nextMarker, xContentType), xContentType), payload);
                    } else {
                        internalAdd(new IndexRequest(index, type, id).routing(routing).version(version).versionType(versionType).create("create".equals(opType)).setPipeline(pipeline).source(sliceTrimmingCarriageReturn(data, from, nextMarker, xContentType), xContentType), payload);
                    }
                } else if ("create".equals(action)) {
                    internalAdd(new IndexRequest(index, type, id).routing(routing).version(version).versionType(versionType).create(true).setPipeline(pipeline).source(sliceTrimmingCarriageReturn(data, from, nextMarker, xContentType), xContentType), payload);
                } else if ("update".equals(action)) {
                    UpdateRequest updateRequest = new UpdateRequest(index, type, id).routing(routing).retryOnConflict(retryOnConflict).version(version).versionType(versionType).routing(routing);
                    try (InputStream dataStream = sliceTrimmingCarriageReturn(data, from, nextMarker, xContentType).streamInput();
                        XContentParser sliceParser = xContent.createParser(NamedXContentRegistry.EMPTY, LoggingDeprecationHandler.INSTANCE, dataStream)) {
                        updateRequest.fromXContent(sliceParser);
                    }
                    if (fetchSourceContext != null) {
                        updateRequest.fetchSource(fetchSourceContext);
                    }
                    IndexRequest upsertRequest = updateRequest.upsertRequest();
                    if (upsertRequest != null) {
                        upsertRequest.version(version);
                        upsertRequest.versionType(versionType);
                        upsertRequest.setPipeline(defaultPipeline);
                    }
                    IndexRequest doc = updateRequest.doc();
                    if (doc != null) {
                        doc.version(version);
                        doc.versionType(versionType);
                    }
                    internalAdd(updateRequest, payload);
                }
                from = nextMarker + 1;
            }
        }
    }
    return this;
}

long[] cidrMaskToMinMax(String cidr){
    Objects.requireNonNull(cidr, "cidr");
    String[] fields = cidr.split("/");
    if (fields.length != 2) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; expected [a.b.c.d, e] but was [%s] after splitting on \"/\" in [%s]", Arrays.toString(fields), cidr));
    }
    if (fields[0].contains(":")) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; expected [a.b.c.d, e] where a, b, c, d are decimal octets " + "but was [%s] after splitting on \"/\" in [%s]", Arrays.toString(fields), cidr));
    }
    byte[] addressBytes;
    try {
        addressBytes = InetAddresses.forString(fields[0]).getAddress();
    } catch (Exception e) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; unable to parse [%s] as an IP address literal", fields[0]), e);
    }
    long accumulator = ((addressBytes[0] & 0xFFL) << 24) + ((addressBytes[1] & 0xFFL) << 16) + ((addressBytes[2] & 0xFFL) << 8) + ((addressBytes[3] & 0xFFL));
    int networkMask;
    try {
        networkMask = Integer.parseInt(fields[1]);
    } catch (NumberFormatException e) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; invalid network mask [%s] in [%s]", fields[1], cidr), e);
    }
    if (networkMask < 0 || networkMask > 32) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; invalid network mask [%s], out of range in [%s]", fields[1], cidr));
    }
    long blockSize = 1L << (32 - networkMask);
    if ((accumulator & (blockSize - 1)) != 0) {
        throw new IllegalArgumentException(String.format(Locale.ROOT, "invalid IPv4/CIDR; invalid address/network mask combination in [%s]; perhaps [%s] was intended?", cidr, octetsToCIDR(longToOctets(accumulator - (accumulator & (blockSize - 1))), networkMask)));
    }
    return new long[] { accumulator, accumulator + blockSize };
}

Map<String, Set<String>> resolveSearchRouting(ClusterState state,String routing,String expressions){
    List<String> resolvedExpressions = expressions != null ? Arrays.asList(expressions) : Collections.<String>emptyList();
    Context context = new Context(state, IndicesOptions.lenientExpandOpen());
    for (ExpressionResolver expressionResolver : expressionResolvers) {
        resolvedExpressions = expressionResolver.resolve(context, resolvedExpressions);
    }
    if (isAllIndices(resolvedExpressions)) {
        return resolveSearchRoutingAllIndices(state.metaData(), routing);
    }
    Map<String, Set<String>> routings = null;
    Set<String> paramRouting = null;
    Set<String> norouting = new HashSet();
    if (routing != null) {
        paramRouting = Sets.newHashSet(Strings.splitStringByCommaToArray(routing));
    }
    for (String expression : resolvedExpressions) {
        AliasOrIndex aliasOrIndex = state.metaData().getAliasAndIndexLookup().get(expression);
        if (aliasOrIndex != null && aliasOrIndex.isAlias()) {
            AliasOrIndex.Alias alias = (AliasOrIndex.Alias) aliasOrIndex;
            for (Tuple<String, AliasMetaData> item : alias.getConcreteIndexAndAliasMetaDatas()) {
                String concreteIndex = item.v1();
                AliasMetaData aliasMetaData = item.v2();
                if (!norouting.contains(concreteIndex)) {
                    if (!aliasMetaData.searchRoutingValues().isEmpty()) {
                        if (routings == null) {
                            routings = new HashMap();
                        }
                        Set<String> r = routings.get(concreteIndex);
                        if (r == null) {
                            r = new HashSet();
                            routings.put(concreteIndex, r);
                        }
                        r.addAll(aliasMetaData.searchRoutingValues());
                        if (paramRouting != null) {
                            r.retainAll(paramRouting);
                        }
                        if (r.isEmpty()) {
                            routings.remove(concreteIndex);
                        }
                    } else {
                        if (!norouting.contains(concreteIndex)) {
                            norouting.add(concreteIndex);
                            if (paramRouting != null) {
                                Set<String> r = new HashSet(paramRouting);
                                if (routings == null) {
                                    routings = new HashMap();
                                }
                                routings.put(concreteIndex, r);
                            } else {
                                if (routings != null) {
                                    routings.remove(concreteIndex);
                                }
                            }
                        }
                    }
                }
            }
        } else {
            if (!norouting.contains(expression)) {
                norouting.add(expression);
                if (paramRouting != null) {
                    Set<String> r = new HashSet(paramRouting);
                    if (routings == null) {
                        routings = new HashMap();
                    }
                    routings.put(expression, r);
                } else {
                    if (routings != null) {
                        routings.remove(expression);
                    }
                }
            }
        }
    }
    if (routings == null || routings.isEmpty()) {
        return null;
    }
    return routings;
}

