int sampleFromDistribution(double[] d,int sampleFromDistribution,double[] d,Random random,int sampleFromDistribution,float[] d,Random random){
    double r = random.nextDouble();
    double total = 0;
    for (int i = 0; i < d.length - 1; i++) {
        if (Float.isNaN(d[i])) {
            throw new RuntimeException("Can't sample from NaN");
        }
        total += d[i];
        if (r < total) {
            return i;
        }
    }
    return d.length - 1;
}

