SimpleMatrix concatenate(SimpleMatrix vectors){
    int size = 0;
    for (SimpleMatrix vector : vectors) {
        size += vector.numRows();
    }
    SimpleMatrix result = new SimpleMatrix(size, 1);
    int index = 0;
    for (SimpleMatrix vector : vectors) {
        result.insertIntoThis(index, 0, vector);
        index += vector.numRows();
    }
    return result;
}

