int getTableAccessOffset(int[] assignment){
    assert (assignment.length == dimensions.length);
    int offset = 0;
    for (int i = 0; i < assignment.length; i++) {
        assert (assignment[i] < dimensions[i]);
        offset = (offset * dimensions[i]) + assignment[i];
    }
    return offset;
}

