BytesRef encodeNumericId(String id){
    byte[] b = new byte[1 + (id.length() + 1) / 2];
    b[0] = (byte) NUMERIC;
    for (int i = 0; i < id.length(); i += 2) {
        int b1 = id.charAt(i) - '0';
        int b2;
        if (i + 1 == id.length()) {
            b2 = 0x0f;
        } else {
            b2 = id.charAt(i + 1) - '0';
        }
        b[1 + i / 2] = (byte) ((b1 << 4) | b2);
    }
    return new BytesRef(b);
}

