VectorMap deserialize(String file,VectorMap deserialize,InputStream in){
    DataInputStream dataIn = new DataInputStream(in);
    itype keyIntType = itype.getType(dataIn.readInt());
    int dim = dataIn.readInt();
    int size = dataIn.readInt();
    VectorMap vectors = new VectorMap();
    for (int i = 0; i < size; ++i) {
        int strlen = keyIntType.read(dataIn);
        byte[] buffer = new byte[strlen];
        if (dataIn.read(buffer, 0, strlen) != strlen) {
            throw new IOException("Could not read string buffer fully!");
        }
        String key = new String(buffer);
        float[] vector = new float[dim];
        for (int k = 0; k < vector.length; ++k) {
            vector[k] = toFloat(dataIn.readShort());
        }
        vectors.put(key, vector);
    }
    return vectors;
}

void standardize(double[] a){
    double m = mean(a);
    if (Double.isNaN(m)) {
        throw new RuntimeException("Can't standardize array whose mean is NaN");
    }
    double s = stdev(a);
    if (s == 0.0 || Double.isNaN(s)) {
        throw new RuntimeException("Can't standardize array whose standard deviation is 0.0 or NaN");
    }
    addInPlace(a, -m);
    multiplyInPlace(a, 1.0 / s);
}

