double sigLevelByApproxRand(double[] A,double[] B,double sigLevelByApproxRand,double[] A,double[] B,int iterations,double sigLevelByApproxRand,int[] A,int[] B,double sigLevelByApproxRand,int[] A,int[] B,int iterations,double sigLevelByApproxRand,boolean[] A,boolean[] B,double sigLevelByApproxRand,boolean[] A,boolean[] B,int iterations){
