HashMap<String, String> readFile(String fileName){
    HashMap<String, String> storage = new HashMap<String, String>();
    try {
        InputStream is = new FileInputStream(fileName);
        int c = is.read();
        StringBuffer str = new StringBuffer();
        int i = 0;
        while (c != -1) {
            if ((char) c == SEPARATOR_DATA) {
                if (i < KEYS.length) {
                    storage.put(KEYS[i], str.toString());
                    str.setLength(0);
                    i++;
                }
            } else {
                str.append((char) c);
            }
            try {
                c = is.read();
            } catch (Exception e) {
                c = -1;
            }
        }
        try {
            is.close();
        } catch (IOException e) {
        }
    } catch (IOException ioe) {
        System.out.println("IOException during processing the file: " + fileName);
    }
    return storage;
}

