String getPunctClass(String punc){
    if (punc.equals("%") || punc.equals("-PLUS-"))
        return "perc";
    else if (punc.startsWith("*"))
        return "bullet";
    else if (sfClass.contains(punc))
        return "sf";
    else if (colonClass.contains(punc) || pEllipsis.matcher(punc).matches())
        return "colon";
    else if (commaClass.contains(punc))
        return "comma";
    else if (currencyClass.contains(punc))
        return "curr";
    else if (slashClass.contains(punc))
        return "slash";
    else if (lBracketClass.contains(punc))
        return "lrb";
    else if (rBracketClass.contains(punc))
        return "rrb";
    else if (quoteClass.contains(punc))
        return "quote";
    return "";
}

Map<String, Object> extractExtendedAttributes(TokenStream stream,Set<String> includeAttributes){
    final Map<String, Object> extendedAttributes = new TreeMap();
    stream.reflectWith((attClass, key, value) -> {
        if (CharTermAttribute.class.isAssignableFrom(attClass)) {
            return;
        }
        if (PositionIncrementAttribute.class.isAssignableFrom(attClass)) {
            return;
        }
        if (OffsetAttribute.class.isAssignableFrom(attClass)) {
            return;
        }
        if (TypeAttribute.class.isAssignableFrom(attClass)) {
            return;
        }
        if (includeAttributes == null || includeAttributes.isEmpty() || includeAttributes.contains(key.toLowerCase(Locale.ROOT))) {
            if (value instanceof BytesRef) {
                final BytesRef p = (BytesRef) value;
                value = p.toString();
            }
            extendedAttributes.put(key, value);
        }
    });
    return extendedAttributes;
}

