boolean updateSettings(Settings toApply,Settings.Builder target,Settings.Builder updates,String type,boolean updateSettings,Settings toApply,Settings.Builder target,Settings.Builder updates,String type,boolean onlyDynamic){
    boolean changed = false;
    final Set<String> toRemove = new HashSet();
    Settings.Builder settingsBuilder = Settings.builder();
    final Predicate<String> canUpdate = (key) -> (isFinalSetting(key) == false && ((onlyDynamic == false && get(key) != null) || isDynamicSetting(key)));
    for (String key : toApply.keySet()) {
        boolean isDelete = toApply.hasValue(key) == false;
        if (isDelete && (isValidDelete(key, onlyDynamic) || key.endsWith("*"))) {
            toRemove.add(key);
        } else if (get(key) == null) {
            throw new IllegalArgumentException(type + " setting [" + key + "], not recognized");
        } else if (isDelete == false && canUpdate.test(key)) {
            validate(key, toApply, false);
            settingsBuilder.copy(key, toApply);
            updates.copy(key, toApply);
            changed = true;
        } else {
            if (isFinalSetting(key)) {
                throw new IllegalArgumentException("final " + type + " setting [" + key + "], not updateable");
            } else {
                throw new IllegalArgumentException(type + " setting [" + key + "], not dynamically updateable");
            }
        }
    }
    changed |= applyDeletes(toRemove, target, k -> isValidDelete(k, onlyDynamic));
    target.put(settingsBuilder.build());
    return changed;
}

FetchResult<T> fetchData(DiscoveryNodes nodes,Set<String> ignoreNodes){
    if (closed) {
        throw new IllegalStateException(shardId + ": can't fetch data on closed async fetch");
    }
    nodesToIgnore.addAll(ignoreNodes);
    fillShardCacheWithDataNodes(cache, nodes);
    List<NodeEntry<T>> nodesToFetch = findNodesToFetch(cache);
    if (nodesToFetch.isEmpty() == false) {
        final long fetchingRound = round.incrementAndGet();
        for (NodeEntry<T> nodeEntry : nodesToFetch) {
            nodeEntry.markAsFetching(fetchingRound);
        }
        DiscoveryNode[] discoNodesToFetch = nodesToFetch.stream().map(NodeEntry::getNodeId).map(nodes::get).toArray(DiscoveryNode[]::new);
        asyncFetch(discoNodesToFetch, fetchingRound);
    }
    if (hasAnyNodeFetching(cache)) {
        return new FetchResult(shardId, null, emptySet());
    } else {
        Map<DiscoveryNode, T> fetchData = new HashMap();
        Set<String> failedNodes = new HashSet();
        for (Iterator<Map.Entry<String, NodeEntry<T>>> it = cache.entrySet().iterator(); it.hasNext(); ) {
            Map.Entry<String, NodeEntry<T>> entry = it.next();
            String nodeId = entry.getKey();
            NodeEntry<T> nodeEntry = entry.getValue();
            DiscoveryNode node = nodes.get(nodeId);
            if (node != null) {
                if (nodeEntry.isFailed()) {
                    it.remove();
                    failedNodes.add(nodeEntry.getNodeId());
                } else {
                    if (nodeEntry.getValue() != null) {
                        fetchData.put(node, nodeEntry.getValue());
                    }
                }
            }
        }
        Set<String> allIgnoreNodes = unmodifiableSet(new HashSet(nodesToIgnore));
        nodesToIgnore.clear();
        if (failedNodes.isEmpty() == false || allIgnoreNodes.isEmpty() == false) {
            reroute(shardId, "nodes failed [" + failedNodes.size() + "], ignored [" + allIgnoreNodes.size() + "]");
        }
        return new FetchResult(shardId, fetchData, allIgnoreNodes);
    }
}

