Map<String, Object> filter(Map<String, ?> map,String[] includes,String[] excludes,Function<Map<String, ?>, Map<String, Object>> filter,String[] includes,String[] excludes,Map<String, Object> filter,Map<String, ?> map,CharacterRunAutomaton includeAutomaton,int initialIncludeState,CharacterRunAutomaton excludeAutomaton,int initialExcludeState,CharacterRunAutomaton matchAllAutomaton,List<Object> filter,Iterable<?> iterable,CharacterRunAutomaton includeAutomaton,int initialIncludeState,CharacterRunAutomaton excludeAutomaton,int initialExcludeState,CharacterRunAutomaton matchAllAutomaton){
    List<Object> filtered = new ArrayList();
    boolean isInclude = includeAutomaton.isAccept(initialIncludeState);
    for (Object value : iterable) {
        if (value instanceof Map) {
            int includeState = includeAutomaton.step(initialIncludeState, '.');
            int excludeState = initialExcludeState;
            if (excludeState != -1) {
                excludeState = excludeAutomaton.step(excludeState, '.');
            }
            Map<String, Object> filteredValue = filter((Map<String, ?>) value, includeAutomaton, includeState, excludeAutomaton, excludeState, matchAllAutomaton);
            if (filteredValue.isEmpty() == false) {
                filtered.add(filteredValue);
            }
        } else if (value instanceof Iterable) {
            List<Object> filteredValue = filter((Iterable<?>) value, includeAutomaton, initialIncludeState, excludeAutomaton, initialExcludeState, matchAllAutomaton);
            if (filteredValue.isEmpty() == false) {
                filtered.add(filteredValue);
            }
        } else if (isInclude) {
            filtered.add(value);
        }
    }
    return filtered;
}

