ReducedQueryPhase reducedQueryPhase(Collection<? extends SearchPhaseResult> queryResults,boolean isScrollRequest,ReducedQueryPhase reducedQueryPhase,Collection<? extends SearchPhaseResult> queryResults,boolean isScrollRequest,boolean trackTotalHits,ReducedQueryPhase reducedQueryPhase,Collection<? extends SearchPhaseResult> queryResults,List<InternalAggregations> bufferedAggs,List<TopDocs> bufferedTopDocs,TopDocsStats topDocsStats,int numReducePhases,boolean isScrollRequest){
    assert numReducePhases >= 0 : "num reduce phases must be >= 0 but was: " + numReducePhases;
    numReducePhases++;
    boolean timedOut = false;
    Boolean terminatedEarly = null;
    if (queryResults.isEmpty()) {
        return new ReducedQueryPhase(topDocsStats.totalHits, topDocsStats.fetchHits, topDocsStats.maxScore, timedOut, terminatedEarly, null, null, null, EMPTY_DOCS, null, null, numReducePhases, false, 0, 0, true);
    }
    final QuerySearchResult firstResult = queryResults.stream().findFirst().get().queryResult();
    final boolean hasSuggest = firstResult.suggest() != null;
    final boolean hasProfileResults = firstResult.hasProfileResults();
    final boolean consumeAggs;
    final List<InternalAggregations> aggregationsList;
    if (bufferedAggs != null) {
        consumeAggs = false;
        assert firstResult.hasAggs() : "firstResult has no aggs but we got non null buffered aggs?";
        aggregationsList = bufferedAggs;
    } else if (firstResult.hasAggs()) {
        aggregationsList = new ArrayList(queryResults.size());
        consumeAggs = true;
    } else {
        aggregationsList = Collections.emptyList();
        consumeAggs = false;
    }
    final Map<String, List<Suggestion>> groupedSuggestions = hasSuggest ? new HashMap() : Collections.emptyMap();
    final Map<String, ProfileShardResult> profileResults = hasProfileResults ? new HashMap(queryResults.size()) : Collections.emptyMap();
    int from = 0;
    int size = 0;
    for (SearchPhaseResult entry : queryResults) {
        QuerySearchResult result = entry.queryResult();
        from = result.from();
        size = result.size();
        if (result.searchTimedOut()) {
            timedOut = true;
        }
        if (result.terminatedEarly() != null) {
            if (terminatedEarly == null) {
                terminatedEarly = result.terminatedEarly();
            } else if (result.terminatedEarly()) {
                terminatedEarly = true;
            }
        }
        if (hasSuggest) {
            assert result.suggest() != null;
            for (Suggestion<? extends Suggestion.Entry<? extends Suggestion.Entry.Option>> suggestion : result.suggest()) {
                List<Suggestion> suggestionList = groupedSuggestions.computeIfAbsent(suggestion.getName(), s -> new ArrayList());
                suggestionList.add(suggestion);
            }
        }
        if (consumeAggs) {
            aggregationsList.add((InternalAggregations) result.consumeAggs());
        }
        if (hasProfileResults) {
            String key = result.getSearchShardTarget().toString();
            profileResults.put(key, result.consumeProfileResult());
        }
    }
    final Suggest suggest = groupedSuggestions.isEmpty() ? null : new Suggest(Suggest.reduce(groupedSuggestions));
    ReduceContext reduceContext = reduceContextFunction.apply(true);
    final InternalAggregations aggregations = aggregationsList.isEmpty() ? null : reduceAggs(aggregationsList, firstResult.pipelineAggregators(), reduceContext);
    final SearchProfileShardResults shardResults = profileResults.isEmpty() ? null : new SearchProfileShardResults(profileResults);
    final SortedTopDocs scoreDocs = this.sortDocs(isScrollRequest, queryResults, bufferedTopDocs, topDocsStats, from, size);
    return new ReducedQueryPhase(topDocsStats.totalHits, topDocsStats.fetchHits, topDocsStats.maxScore, timedOut, terminatedEarly, suggest, aggregations, shardResults, scoreDocs.scoreDocs, scoreDocs.sortFields, firstResult != null ? firstResult.sortValueFormats() : null, numReducePhases, scoreDocs.isSortedByField, size, from, firstResult == null);
}

