Operation readOperation(Location location,Operation readOperation,StreamInput input,Translog.Operation readOperation,BufferedChecksumStreamInput in){
    final Translog.Operation operation;
    try {
        final int opSize = in.readInt();
        if (opSize < 4) {
            throw new TranslogCorruptedException(in.getSource(), "operation size must be at least 4 but was: " + opSize);
        }
        in.resetDigest();
        if (in.markSupported()) {
            in.mark(opSize);
            in.skip(opSize - 4);
            verifyChecksum(in);
            in.reset();
        }
        operation = Translog.Operation.readOperation(in);
        verifyChecksum(in);
    } catch (EOFException e) {
        throw new TruncatedTranslogException(in.getSource(), "reached premature end of file, translog is truncated", e);
    }
    return operation;
}

