void maybeSyncGlobalCheckpoint(String reason){
    verifyNotClosed();
    assert shardRouting.primary() : "only call maybeSyncGlobalCheckpoint on primary shard";
    if (replicationTracker.isPrimaryMode() == false) {
        return;
    }
    assert assertPrimaryMode();
    final SeqNoStats stats = getEngine().getSeqNoStats(replicationTracker.getGlobalCheckpoint());
    if (stats.getMaxSeqNo() == stats.getGlobalCheckpoint()) {
        final ObjectLongMap<String> globalCheckpoints = getInSyncGlobalCheckpoints();
        final String allocationId = routingEntry().allocationId().getId();
        assert globalCheckpoints.containsKey(allocationId);
        final long globalCheckpoint = globalCheckpoints.get(allocationId);
        final boolean syncNeeded = StreamSupport.stream(globalCheckpoints.values().spliterator(), false).anyMatch(v -> v.value < globalCheckpoint);
        if (syncNeeded) {
            logger.trace("syncing global checkpoint for [{}]", reason);
            globalCheckpointSyncer.run();
        }
    }
}

Set<ShardId> selectShrinkShards(int shardId,IndexMetaData sourceIndexMetadata,int numTargetShards){
    if (shardId >= numTargetShards) {
        throw new IllegalArgumentException("the number of target shards (" + numTargetShards + ") must be greater than the shard id: " + shardId);
    }
    if (sourceIndexMetadata.getNumberOfShards() < numTargetShards) {
        throw new IllegalArgumentException("the number of target shards [" + numTargetShards + "] must be less that the number of source shards [" + sourceIndexMetadata.getNumberOfShards() + "]");
    }
    int routingFactor = getRoutingFactor(sourceIndexMetadata.getNumberOfShards(), numTargetShards);
    Set<ShardId> shards = new HashSet(routingFactor);
    for (int i = shardId * routingFactor; i < routingFactor * shardId + routingFactor; i++) {
        shards.add(new ShardId(sourceIndexMetadata.getIndex(), i));
    }
    return shards;
}

