    final long localCheckpoint = getLocalCheckpoint();
    if (globalCheckpoint > localCheckpoint) {
        assert state() != IndexShardState.POST_RECOVERY && state() != IndexShardState.STARTED : "supposedly in-sync shard copy received a global checkpoint [" + globalCheckpoint + "] " + "that is higher than its local checkpoint [" + localCheckpoint + "]";
        return;
    }
    replicationTracker.updateGlobalCheckpointOnReplica(globalCheckpoint, reason);
}

