CoordinateNode parseCoordinates(XContentParser parser,boolean ignoreZValue){
    if (parser.currentToken() == XContentParser.Token.START_OBJECT) {
        parser.skipChildren();
        parser.nextToken();
        throw new ElasticsearchParseException("coordinates cannot be specified as objects");
    }
    XContentParser.Token token = parser.nextToken();
    if (token != XContentParser.Token.START_ARRAY && token != XContentParser.Token.END_ARRAY && token != XContentParser.Token.VALUE_NULL) {
        return new CoordinateNode(parseCoordinate(parser, ignoreZValue));
    } else if (token == XContentParser.Token.VALUE_NULL) {
        throw new IllegalArgumentException("coordinates cannot contain NULL values)");
    }
    List<CoordinateNode> nodes = new ArrayList();
    while (token != XContentParser.Token.END_ARRAY) {
        CoordinateNode node = parseCoordinates(parser, ignoreZValue);
        if (nodes.isEmpty() == false && nodes.get(0).numDimensions() != node.numDimensions()) {
            throw new ElasticsearchParseException("Exception parsing coordinates: number of dimensions do not match");
        }
        nodes.add(node);
        token = parser.nextToken();
    }
    return new CoordinateNode(nodes);
}

double logLikelihoodScratch(){
    double s = 0;
    for (int i = 0; i < probConds.length; i++) {
        for (int j = 0; j < probConds[i].length; j++) {
            probConds[i][j] = 0;
        }
        zlambda[i] = 0;
    }
    Experiments exp = p.data;
    for (int fNo = 0, fSize = p.fSize; fNo < fSize; fNo++) {
        Feature f = p.functions.get(fNo);
        double fLambda = lambda[fNo];
        double sum = ftildeArr[fNo];
        sum *= exp.getNumber();
        s -= sum * fLambda;
        if (Math.abs(fLambda) > 200) {
            log.info("lambda " + fNo + " too big: " + fLambda);
        }
        for (int i = 0, length = f.len(); i < length; i++) {
            int x = f.getX(i);
            int y = f.getY(i);
            if (ASSUME_BINARY) {
                probConds[x][y] += fLambda;
            } else {
                double val = f.getVal(i);
                probConds[x][y] += (val * fLambda);
            }
        }
    }
    for (int x = 0; x < probConds.length; x++) {
        zlambda[x] = ArrayMath.logSum(probConds[x]);
        s += zlambda[x] * exp.ptildeX(x) * exp.getNumber();
        for (int y = 0; y < probConds[x].length; y++) {
            probConds[x][y] = divide(probConds[x][y], zlambda[x]);
        }
    }
    if (s < 0) {
        throw new IllegalStateException("neg log lik smaller than 0: " + s);
    }
    return s;
}

