void executeBulkItemRequest(BulkPrimaryExecutionContext context,UpdateHelper updateHelper,LongSupplier nowInMillisSupplier,MappingUpdatePerformer mappingUpdater,CheckedRunnable<Exception> waitForMappingUpdate){
    final DocWriteRequest.OpType opType = context.getCurrent().opType();
    final UpdateHelper.Result updateResult;
    if (opType == DocWriteRequest.OpType.UPDATE) {
        final UpdateRequest updateRequest = (UpdateRequest) context.getCurrent();
        try {
            updateResult = updateHelper.prepare(updateRequest, context.getPrimary(), nowInMillisSupplier);
        } catch (Exception failure) {
            final Engine.Result result = new Engine.IndexResult(failure, updateRequest.version(), SequenceNumbers.UNASSIGNED_SEQ_NO);
            context.setRequestToExecute(updateRequest);
            context.markOperationAsExecuted(result);
            context.markAsCompleted(context.getExecutionResult());
            return;
        }
        switch(updateResult.getResponseResult()) {
            case CREATED:
            case UPDATED:
                IndexRequest indexRequest = updateResult.action();
                IndexMetaData metaData = context.getPrimary().indexSettings().getIndexMetaData();
                MappingMetaData mappingMd = metaData.mappingOrDefault(indexRequest.type());
                indexRequest.process(metaData.getCreationVersion(), mappingMd, updateRequest.concreteIndex());
                context.setRequestToExecute(indexRequest);
                break;
            case DELETED:
                context.setRequestToExecute(updateResult.action());
                break;
            case NOOP:
                context.markOperationAsNoOp(updateResult.action());
                context.markAsCompleted(context.getExecutionResult());
                return;
            default:
                throw new IllegalStateException("Illegal update operation " + updateResult.getResponseResult());
        }
    } else {
        context.setRequestToExecute(context.getCurrent());
        updateResult = null;
    }
    assert context.getRequestToExecute() != null;
    if (context.getRequestToExecute().opType() == DocWriteRequest.OpType.DELETE) {
        executeDeleteRequestOnPrimary(context, mappingUpdater);
    } else {
        executeIndexRequestOnPrimary(context, mappingUpdater);
    }
    if (context.requiresWaitingForMappingUpdate()) {
        try {
            waitForMappingUpdate.run();
            context.resetForExecutionForRetry();
        } catch (Exception e) {
            context.failOnMappingUpdate(e);
        }
        return;
    }
    assert context.isOperationExecuted();
    if (opType == DocWriteRequest.OpType.UPDATE && context.getExecutionResult().isFailed() && isConflictException(context.getExecutionResult().getFailure().getCause())) {
        final UpdateRequest updateRequest = (UpdateRequest) context.getCurrent();
        if (context.getRetryCounter() < updateRequest.retryOnConflict()) {
            context.resetForExecutionForRetry();
            return;
        }
    }
    finalizePrimaryOperationOnCompletion(context, opType, updateResult);
}

void takeAdaGradientStep(Cost cost,double adaAlpha,double adaEps){
    validateTraining();
    double[][] gradW1 = cost.getGradW1(), gradW2 = cost.getGradW2(), gradE = cost.getGradE();
    double[] gradb1 = cost.getGradb1();
    for (int i = 0; i < W1.length; ++i) {
        for (int j = 0; j < W1[i].length; ++j) {
            eg2W1[i][j] += gradW1[i][j] * gradW1[i][j];
            W1[i][j] -= adaAlpha * gradW1[i][j] / Math.sqrt(eg2W1[i][j] + adaEps);
        }
    }
    for (int i = 0; i < b1.length; ++i) {
        eg2b1[i] += gradb1[i] * gradb1[i];
        b1[i] -= adaAlpha * gradb1[i] / Math.sqrt(eg2b1[i] + adaEps);
    }
    for (int i = 0; i < W2.length; ++i) {
        for (int j = 0; j < W2[i].length; ++j) {
            eg2W2[i][j] += gradW2[i][j] * gradW2[i][j];
            W2[i][j] -= adaAlpha * gradW2[i][j] / Math.sqrt(eg2W2[i][j] + adaEps);
        }
    }
    if (config.doWordEmbeddingGradUpdate) {
        for (int i = 0; i < E.length; ++i) {
            for (int j = 0; j < E[i].length; ++j) {
                eg2E[i][j] += gradE[i][j] * gradE[i][j];
                E[i][j] -= adaAlpha * gradE[i][j] / Math.sqrt(eg2E[i][j] + adaEps);
            }
        }
    }
}

void waitForActiveShards(String[] indexNames,ActiveShardCount activeShardCount,TimeValue timeout,Consumer<Boolean> onResult,Consumer<Exception> onFailure){
    if (activeShardCount == ActiveShardCount.NONE) {
        onResult.accept(true);
        return;
    }
    final ClusterState state = clusterService.state();
    final ClusterStateObserver observer = new ClusterStateObserver(state, clusterService, null, logger, threadPool.getThreadContext());
    if (activeShardCount.enoughShardsActive(state, indexNames)) {
        onResult.accept(true);
    } else {
        final Predicate<ClusterState> shardsAllocatedPredicate = newState -> activeShardCount.enoughShardsActive(newState, indexNames);
        final ClusterStateObserver.Listener observerListener = new ClusterStateObserver.Listener() {
            @Override
            public void onNewClusterState(ClusterState state) {
                onResult.accept(true);
            }
            @Override
            public void onClusterServiceClose() {
                logger.debug("[{}] cluster service closed while waiting for enough shards to be started.", Arrays.toString(indexNames));
                onFailure.accept(new NodeClosedException(clusterService.localNode()));
            }
            @Override
            public void onTimeout(TimeValue timeout) {
                onResult.accept(false);
            }
        };
        observer.waitForNextChange(observerListener, shardsAllocatedPredicate, timeout);
    }
}

