T remove(long key){
    for (long i = slot(hash(key), mask); ; i = nextSlot(i, mask)) {
        final T previous = values.set(i, null);
        if (previous == null) {
            return null;
        } else if (keys.get(i) == key) {
            --size;
            for (long j = nextSlot(i, mask); used(j); j = nextSlot(j, mask)) {
