List<T> rotate(List<T> list,int distance){
    if (list.isEmpty()) {
        return list;
    }
    int d = distance % list.size();
    if (d < 0) {
        d += list.size();
    }
    if (d == 0) {
        return list;
    }
    return new RotatedList(list, d);
}

