void exportFile(Long fileId,List<String> ruleNames,Supplier<Boolean> cancelCheck){
    AbstractFile file = Case.getCurrentCaseThrows().getSleuthkitCase().getAbstractFileById(fileId);
    if (!shouldExportFile(file)) {
        return;
    }
    Map<BlackboardArtifact, List<BlackboardAttribute>> artifactsToAttributes = new HashMap();
    List<BlackboardArtifact> artifacts = file.getAllArtifacts();
    for (BlackboardArtifact artifact : artifacts) {
        artifactsToAttributes.put(artifact, artifact.getAttributes());
    }
    Path filePath = exportFileToSecondaryStorage(file, cancelCheck);
    if (filePath == null) {
        return;
    }
    addFileToCatalog(file, artifactsToAttributes, filePath, masterCatalog);
    for (String ruleName : ruleNames) {
        JsonGenerator ruleCatalog = this.ruleNamesToCatalogs.get(ruleName);
        if (null == ruleCatalog) {
            Path catalogPath = Paths.get(reportsDirPath.toString(), ruleName, "catalog.json");
            Files.createDirectories(catalogPath.getParent());
            File catalogFile = catalogPath.toFile();
            ruleCatalog = jsonGeneratorFactory.createGenerator(catalogFile, JsonEncoding.UTF8);
            ruleNamesToCatalogs.put(ruleName, ruleCatalog);
        }
        addFileToCatalog(file, artifactsToAttributes, filePath, ruleCatalog);
    }
}

List<String> extractLogicalEvidenceFileContents(List<String> logicalEvidenceFilePaths){
    final List<String> extractedPaths = new ArrayList();
    Path ewfexportPath;
    ewfexportPath = locateEwfexportExecutable();
    List<String> command = new ArrayList();
    for (final String l01Path : logicalEvidenceFilePaths) {
        command.clear();
        command.add(ewfexportPath.toAbsolutePath().toString());
        command.add("-f");
        command.add("files");
        command.add("-t");
        File l01Dir = new File(Case.getCurrentCaseThrows().getModuleDirectory(), L01_EXTRACTION_DIR);
        if (!l01Dir.exists()) {
            l01Dir.mkdirs();
        }
        Path dirPath = Paths.get(FilenameUtils.getBaseName(l01Path) + UNIQUENESS_CONSTRAINT_SEPERATOR + System.currentTimeMillis());
        command.add(dirPath.toString());
        command.add(l01Path);
        ProcessBuilder processBuilder = new ProcessBuilder(command);
        processBuilder.directory(l01Dir);
        try {
            Path logFileName = Paths.get(l01Dir.toString(), dirPath.toString() + LOG_FILE_EXTENSION);
            File logFile = new File(logFileName.toString());
            Path errFileName = Paths.get(l01Dir.toString(), dirPath.toString() + LOG_FILE_EXTENSION);
            File errFile = new File(errFileName.toString());
            processBuilder.redirectError(ProcessBuilder.Redirect.appendTo(errFile));
            processBuilder.redirectOutput(ProcessBuilder.Redirect.appendTo(logFile));
            ExecUtil.execute(processBuilder, new ExecUtil.TimedProcessTerminator());
            if (l01Dir.toPath().resolve(dirPath).toFile().exists()) {
                extractedPaths.add(l01Dir.toPath().resolve(dirPath).toString());
            } else {
                throw new L01Exception("Can not process the selected L01 file, ewfExport was unable to extract any files from it.");
            }
        } catch (SecurityException ex) {
            throw new L01Exception("Security exception occcured while trying to extract l01 contents", ex);
        } catch (IOException ex) {
            throw new L01Exception("IOException occcured while trying to extract l01 contents", ex);
        }
    }
    return extractedPaths;
}

