CorrelationAttributeInstance getCorrelationAttributeInstance(CorrelationAttributeInstance.Type type,CorrelationCase correlationCase,CorrelationDataSource correlationDataSource,long objectID,CorrelationAttributeInstance getCorrelationAttributeInstance,CorrelationAttributeInstance.Type type,CorrelationCase correlationCase,CorrelationDataSource correlationDataSource,String value,String filePath){
    if (correlationCase == null) {
        throw new EamDbException("Correlation case is null");
    }
    if (correlationDataSource == null) {
        throw new EamDbException("Correlation data source is null");
    }
    if (filePath == null) {
        throw new EamDbException("Correlation file path is null");
    }
    Connection conn = connect();
    PreparedStatement preparedStatement = null;
    ResultSet resultSet = null;
    CorrelationAttributeInstance correlationAttributeInstance = null;
    try {
        String normalizedValue = CorrelationAttributeNormalizer.normalize(type, value);
        String tableName = EamDbUtil.correlationTypeToInstanceTableName(type);
        String sql = "SELECT id, known_status, comment FROM " + tableName + " WHERE case_id=?" + " AND data_source_id=?" + " AND value=?" + " AND file_path=?";
        preparedStatement = conn.prepareStatement(sql);
        preparedStatement.setInt(1, correlationCase.getID());
        preparedStatement.setInt(2, correlationDataSource.getID());
        preparedStatement.setString(3, normalizedValue);
        preparedStatement.setString(4, filePath.toLowerCase());
        resultSet = preparedStatement.executeQuery();
        if (resultSet.next()) {
            int instanceId = resultSet.getInt(1);
            int knownStatus = resultSet.getInt(2);
            String comment = resultSet.getString(3);
            correlationAttributeInstance = new CorrelationAttributeInstance(type, value, instanceId, correlationCase, correlationDataSource, filePath, comment, TskData.FileKnown.valueOf((byte) knownStatus), null);
        }
    } catch (SQLException ex) {
        throw new EamDbException("Error getting notable artifact instances.", ex);
    } finally {
        EamDbUtil.closeStatement(preparedStatement);
        EamDbUtil.closeResultSet(resultSet);
        EamDbUtil.closeConnection(conn);
    }
    return correlationAttributeInstance;
}

