Comparator<Node> getComparator(){
    Comparator<Node> comp = (node1, node2) -> 0;
    if (!(parent instanceof TableFilterNode)) {
        return comp;
    } else {
        List<SortCriterion> sortCriteria = loadSortCriteria((TableFilterNode) parent);
        return // default to unordered if nothing is persisted
        sortCriteria.stream().map(this::getCriterionComparator).collect(Collectors.reducing(Comparator::thenComparing)).orElse(comp);
    }
}

