int hashCode(int hashCode){
    if (fast) {
        int h = 0;
        for (Entry<K, V> e : map.entrySet()) {
            h += e.hashCode();
        }
        return (h);
    } else {
        synchronized (map) {
            int h = 0;
            for (Entry<K, V> e : map.entrySet()) {
                h += e.hashCode();
            }
            return (h);
        }
    }
}

