Set<Long> deleteTag(long objectID,Long artifactID,long tagID,boolean stillTagged){
    DBLock.lock();
    try {
        deleteTagStmt.clearParameters();
        deleteTagStmt.setLong(1, tagID);
        deleteTagStmt.executeUpdate();
        return markEventsTagged(objectID, artifactID, stillTagged);
    } catch (SQLException ex) {
        LOGGER.log(Level.SEVERE, "failed to add tag to event", ex);
    } finally {
        DBLock.unlock();
    }
    return Collections.emptySet();
}

String copyFile(String source,String destFolder,String newName,String ext,boolean overwrite){
    final String destFileName = destFolder + File.separator + newName + ext;
    final File destFile = new File(destFileName);
    if (destFile.exists()) {
        if (overwrite) {
            destFile.delete();
        } else {
            return null;
        }
    }
    final FileObject sourceFileObj = org.openide.filesystems.FileUtil.createData(new File(source));
    final FileObject destFolderObj = org.openide.filesystems.FileUtil.createData(new File(destFolder));
    FileObject created = org.openide.filesystems.FileUtil.copyFile(sourceFileObj, destFolderObj, newName, ext.substring(1));
    return created.getPath();
}

