String makeSnippet(String content,Matcher hitMatcher,String hit){
    int maxIndex = content.length() - 1;
    final int end = hitMatcher.end();
    final int start = hitMatcher.start();
    return content.substring(Integer.max(0, start - 20), Integer.max(0, start)) + SNIPPET_DELIMITER + hit + SNIPPET_DELIMITER + content.substring(Integer.min(maxIndex, end), Integer.min(maxIndex, end + 20));
}

Map<EventType, Long> countEventsByType(ZoomParams params,Map<EventType, Long> countEventsByType,Long startTime,Long endTime,RootFilter filter,EventTypeZoomLevel zoomLevel){
    if (Objects.equals(startTime, endTime)) {
        endTime++;
    }
    Map<EventType, Long> typeMap = new HashMap();
    final boolean useSubTypes = (zoomLevel == EventTypeZoomLevel.SUB_TYPE);
    final // NON-NLS
    String queryString = "SELECT count(DISTINCT events.event_id) AS count, " + typeColumnHelper(useSubTypes) + " FROM events" + useHashHitTablesHelper(filter) + useTagTablesHelper(filter) + " WHERE time >= " + startTime + " AND time < " + endTime + " AND " + SQLHelper.getSQLWhere(filter) + " GROUP BY " + typeColumnHelper(useSubTypes);
    DBLock.lock();
    try (Statement stmt = con.createStatement();
        ResultSet rs = stmt.executeQuery(queryString)) {
        while (rs.next()) {
            EventType type = useSubTypes ? RootEventType.allTypes.get(rs.getInt("sub_type")) : BaseTypes.values()[rs.getInt("base_type")];
            typeMap.put(type, rs.getLong("count"));
        }
    } catch (Exception ex) {
        LOGGER.log(Level.SEVERE, "Error getting count of events from db.", ex);
    } finally {
        DBLock.unlock();
    }
    return typeMap;
}

