void log(JSONObject event){
    if (!isEventLoggingEnabled()) {
        return;
    }
    Completable.fromAction(() -> {
        String eventStr = event.toString();
        String dataURL = Uri.parse(EVENTLOG_URL).buildUpon().query(eventStr).build().toString();
        if (ReleaseUtil.isDevRelease()) {
            L.d(eventStr);
        }
        if (dataURL.length() > MAX_URL_LEN) {
            L.logRemoteErrorIfProd(new RemoteLogException("EventLogging max length exceeded").put("length", String.valueOf(dataURL.length())));
        }
        Request request = new Request.Builder().url(dataURL).post(EMPTY_REQ).build();
        OkHttpConnectionFactory.getClient().newCall(request).execute().close();
    }).subscribeOn(Schedulers.io()).subscribe(() -> {
    }, throwable -> L.d("Lost EL data: " + event.toString()));
}

