void getRecentDocuments(){
    org.sleuthkit.autopsy.casemodule.services.FileManager fileManager = currentCase.getServices().getFileManager();
    List<AbstractFile> recentFiles;
    try {
        recentFiles = fileManager.findFiles(dataSource, "%.lnk", "Recent");
    } catch (TskCoreException ex) {
        logger.log(Level.WARNING, "Error searching for .lnk files.");
        this.addErrorMessage(NbBundle.getMessage(this.getClass(), "RecentDocumentsByLnk.getRecDoc.errMsg.errGetLnkFiles", this.getName()));
        return;
    }
    if (recentFiles.isEmpty()) {
        logger.log(Level.INFO, "Didn't find any recent files.");
        return;
    }
    dataFound = true;
    for (AbstractFile recentFile : recentFiles) {
        if (context.dataSourceIngestIsCancelled()) {
            break;
        }
        if (recentFile.getSize() == 0) {
            continue;
        }
        JLNK lnk;
        JLnkParser lnkParser = new JLnkParser(new ReadContentInputStream(recentFile), (int) recentFile.getSize());
        try {
            lnk = lnkParser.parse();
        } catch (JLnkParserException e) {
            boolean unalloc = recentFile.isMetaFlagSet(TskData.TSK_FS_META_FLAG_ENUM.UNALLOC) || recentFile.isDirNameFlagSet(TskData.TSK_FS_NAME_FLAG_ENUM.UNALLOC);
            if (unalloc == false) {
                logger.log(Level.WARNING, "Error lnk parsing the file to get recent files {0}", recentFile);
            }
            continue;
        }
        Collection<BlackboardAttribute> bbattributes = new ArrayList();
        String path = lnk.getBestPath();
        bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH, NbBundle.getMessage(this.getClass(), "RecentDocumentsByLnk.parentModuleName.noSpace"), path));
        bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_PATH_ID, NbBundle.getMessage(this.getClass(), "RecentDocumentsByLnk.parentModuleName.noSpace"), Util.findID(dataSource, path)));
        bbattributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_DATETIME, NbBundle.getMessage(this.getClass(), "RecentDocumentsByLnk.parentModuleName.noSpace"), recentFile.getCrtime()));
        this.addArtifact(ARTIFACT_TYPE.TSK_RECENT_OBJECT, recentFile, bbattributes);
    }
    services.fireModuleDataEvent(new ModuleDataEvent(NbBundle.getMessage(this.getClass(), "RecentDocumentsByLnk.parentModuleName"), BlackboardArtifact.ARTIFACT_TYPE.TSK_RECENT_OBJECT));
}

