    }
    try {
        for (BlackboardArtifact artifact : skCase.getBlackboardArtifacts(BlackboardArtifact.ARTIFACT_TYPE.TSK_GPS_TRACKPOINT)) {
            try {
                Long timestamp = getLong(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_DATETIME);
                String desc = getDescriptionFromArtifact(artifact, "GPS Trackpoint");
                Double lat = getDouble(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_GEO_LATITUDE);
                Double lon = getDouble(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_GEO_LONGITUDE);
                Double alt = getDouble(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_GEO_ALTITUDE);
                Element point = makePoint(lat, lon, alt);
                String formattedCoordinates = String.format("%.2f, %.2f, %.2f", lat, lon, alt);
                String trackName = getString(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_NAME);
                if (trackName == null || trackName.isEmpty()) {
                    trackName = getString(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_PROG_NAME);
                }
                if (trackName == null || trackName.isEmpty()) {
                    trackName = getString(artifact, BlackboardAttribute.ATTRIBUTE_TYPE.TSK_FLAG);
                }
                if (trackName == null || trackName.isEmpty()) {
                    trackName = "GPS Trackpoint";
                }
                gpsTrackpointsFolder.addContent(makePlacemark(trackName, FeatureColor.YELLOW, desc, timestamp, point, formattedCoordinates));
            } catch (Exception ex) {
                logger.log(Level.SEVERE, "Could not extract Trackpoint information.", ex);
                result = ReportProgressPanel.ReportStatus.ERROR;
            }
        }
    } catch (TskCoreException ex) {
        logger.log(Level.SEVERE, "Could not get GPS Trackpoints from database.", ex);
        result = ReportProgressPanel.ReportStatus.ERROR;
    }
    try {
        InputStream input = getClass().getResourceAsStream(STYLESHEETS_PATH + KML_STYLE_FILE);
        OutputStream output = new FileOutputStream(baseReportDir + KML_STYLE_FILE);
        FileUtil.copy(input, output);
    } catch (IOException ex) {
        logger.log(Level.SEVERE, "Error placing KML stylesheet. The .KML file will not function properly.", ex);
        result = ReportProgressPanel.ReportStatus.ERROR;
    }
    try (FileOutputStream writer = new FileOutputStream(kmlFileFullPath)) {
        XMLOutputter outputter = new XMLOutputter(Format.getPrettyFormat());
        outputter.output(kmlDocument, writer);
        String prependedStatus = "";
        if (result == ReportProgressPanel.ReportStatus.ERROR) {
            prependedStatus = "Incomplete ";
        }
        Case.getCurrentCaseThrows().addReport(kmlFileFullPath, NbBundle.getMessage(this.getClass(), "ReportKML.genReport.srcModuleName.text"), prependedStatus + NbBundle.getMessage(this.getClass(), "ReportKML.genReport.reportName"));
    } catch (IOException ex) {
        logger.log(Level.SEVERE, "Could not write the KML file.", ex);
        progressPanel.complete(ReportProgressPanel.ReportStatus.ERROR);
    } catch (TskCoreException ex) {
        String errorMessage = String.format("Error adding %s to case as a report", kmlFileFullPath);
        logger.log(Level.SEVERE, errorMessage, ex);
        result = ReportProgressPanel.ReportStatus.ERROR;
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
        result = ReportProgressPanel.ReportStatus.ERROR;
    }
    progressPanel.complete(result);
}

void writeSingleFileInboxMessage(BlackboardArtifact artifact,Content hitContent){
    StringBuilder subjectSb = new StringBuilder(1024);
    if (!query.isLiteral()) {
        subjectSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.regExpHitLbl"));
    } else {
        subjectSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.kwHitLbl"));
    }
    StringBuilder detailsSb = new StringBuilder(1024);
    String uniqueKey = null;
    BlackboardAttribute attr = artifact.getAttribute(new BlackboardAttribute.Type(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD));
    if (attr != null) {
        final String keyword = attr.getValueString();
        subjectSb.append(keyword);
        uniqueKey = keyword.toLowerCase();
        detailsSb.append("<table border='0' cellpadding='4' width='280'>");
        detailsSb.append("<tr>");
        detailsSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.kwHitThLbl"));
        detailsSb.append("<td>").append(EscapeUtil.escapeHtml(keyword)).append("</td>");
        detailsSb.append("</tr>");
    }
    attr = artifact.getAttribute(new BlackboardAttribute.Type(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD_PREVIEW));
    if (attr != null) {
        detailsSb.append("<tr>");
        detailsSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.previewThLbl"));
        detailsSb.append("<td>").append(EscapeUtil.escapeHtml(attr.getValueString())).append("</td>");
        detailsSb.append("</tr>");
    }
    detailsSb.append("<tr>");
    detailsSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.fileThLbl"));
    if (hitContent instanceof AbstractFile) {
        AbstractFile hitFile = (AbstractFile) hitContent;
        detailsSb.append("<td>").append(hitFile.getParentPath()).append(hitFile.getName()).append("</td>");
    } else {
        detailsSb.append("<td>").append(hitContent.getName()).append("</td>");
    }
    detailsSb.append("</tr>");
    attr = artifact.getAttribute(new BlackboardAttribute.Type(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME));
    if (attr != null) {
        detailsSb.append("<tr>");
        detailsSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.listThLbl"));
        detailsSb.append("<td>").append(attr.getValueString()).append("</td>");
        detailsSb.append("</tr>");
    }
    if (!query.isLiteral()) {
        attr = artifact.getAttribute(new BlackboardAttribute.Type(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD_REGEXP));
        if (attr != null) {
            detailsSb.append("<tr>");
            detailsSb.append(NbBundle.getMessage(this.getClass(), "KeywordSearchIngestModule.regExThLbl"));
            detailsSb.append("<td>").append(attr.getValueString()).append("</td>");
            detailsSb.append("</tr>");
        }
    }
    detailsSb.append("</table>");
    IngestServices.getInstance().postMessage(IngestMessage.createDataMessage(MODULE_NAME, subjectSb.toString(), detailsSb.toString(), uniqueKey, artifact));
}

Map<String, List<AbstractFile>> findFilesBymd5(List<String> md5Hash,Map<String, List<AbstractFile>> findFilesBymd5,List<String> md5Hash,ProgressHandle progress,SwingWorker<Object, Void> worker){
    Map<String, List<AbstractFile>> map = new LinkedHashMap<String, List<AbstractFile>>();
    if (!worker.isCancelled()) {
        progress.switchToDeterminate(md5Hash.size());
        int size = 0;
        for (String md5 : md5Hash) {
            if (worker.isCancelled()) {
                break;
            }
            List<AbstractFile> files = findFilesByMd5(md5);
            if (!files.isEmpty()) {
                map.put(md5, files);
            }
            size++;
            if (!worker.isCancelled()) {
                progress.progress(size);
            }
        }
    }
    return map;
}

