void findTexts(IngestJobContext context){
    Case openCase;
    try {
        openCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
        return;
    }
    blackboard = openCase.getServices().getBlackboard();
    try {
        SleuthkitCase skCase = openCase.getSleuthkitCase();
        absFiles = skCase.findAllFilesWhere("name ='mmssms.db'");
        if (absFiles.isEmpty()) {
            return;
        }
        for (AbstractFile file : absFiles) {
            try {
                jFile = new java.io.File(Case.getCurrentCaseThrows().getTempDirectory(), file.getName().replaceAll("[<>%|\"/:*\\\\]", ""));
                dbPath = jFile.toString();
                fileId = file.getId();
                ContentUtils.writeToFile(file, jFile, context::dataSourceIngestIsCancelled);
                findTextsInDB(dbPath, fileId);
            } catch (ReadContentInputStream.ReadContentInputStreamException ex) {
                logger.log(Level.WARNING, String.format("Error reading content from file '%s' (id=%d).", file.getName(), fileId), ex);
            } catch (Exception ex) {
                logger.log(Level.SEVERE, String.format("Error writing content from file '%s' (id=%d) to '%s'.", file.getName(), fileId, dbPath), ex);
            }
        }
    } catch (TskCoreException e) {
        logger.log(Level.SEVERE, "Error finding text messages", e);
    }
