BlackboardArtifact postKeywordHitToBlackboard(Content content,Keyword foundKeyword,KeywordHit hit,String snippet,String listName){
    final String MODULE_NAME = KeywordSearchModuleFactory.getModuleName();
    if (content == null) {
        LOGGER.log(Level.WARNING, "Error adding artifact for keyword hit to blackboard");
        return null;
    }
    if (originalKeyword.getArtifactAttributeType() == ATTRIBUTE_TYPE.TSK_CARD_NUMBER) {
        createCCNAccount(content, foundKeyword, hit, snippet, listName);
        return null;
    }
    BlackboardArtifact newArtifact;
    Collection<BlackboardAttribute> attributes = new ArrayList();
    attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_KEYWORD, MODULE_NAME, foundKeyword.getSearchTerm()));
    attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_KEYWORD_REGEXP, MODULE_NAME, getQueryString()));
    try {
        newArtifact = content.newArtifact(ARTIFACT_TYPE.TSK_KEYWORD_HIT);
    } catch (TskCoreException ex) {
        LOGGER.log(Level.SEVERE, "Error adding artifact for keyword hit to blackboard", ex);
        return null;
    }
    if (StringUtils.isNotBlank(listName)) {
        attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_SET_NAME, MODULE_NAME, listName));
    }
    if (snippet != null) {
        attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_KEYWORD_PREVIEW, MODULE_NAME, snippet));
    }
    hit.getArtifactID().ifPresent(artifactID -> attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_ASSOCIATED_ARTIFACT, MODULE_NAME, artifactID)));
    if (originalKeyword.searchTermIsLiteral()) {
        attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_KEYWORD_SEARCH_TYPE, MODULE_NAME, KeywordSearch.QueryType.SUBSTRING.ordinal()));
    } else {
        attributes.add(new BlackboardAttribute(ATTRIBUTE_TYPE.TSK_KEYWORD_SEARCH_TYPE, MODULE_NAME, KeywordSearch.QueryType.REGEX.ordinal()));
    }
    try {
        newArtifact.addAttributes(attributes);
        return newArtifact;
    } catch (TskCoreException e) {
        LOGGER.log(Level.SEVERE, "Error adding bb attributes for terms search artifact", e);
        return null;
    }
}

void addEmailMsgProperty(Map<String, Object> map,BlackboardAttribute attribute){
    final int attributeTypeID = attribute.getAttributeType().getTypeID();
    if (attributeTypeID == ATTRIBUTE_TYPE.TSK_DATETIME_SENT.getTypeID() || attributeTypeID == ATTRIBUTE_TYPE.TSK_EMAIL_CONTENT_HTML.getTypeID() || attributeTypeID == ATTRIBUTE_TYPE.TSK_EMAIL_CONTENT_RTF.getTypeID() || attributeTypeID == ATTRIBUTE_TYPE.TSK_EMAIL_BCC.getTypeID() || attributeTypeID == ATTRIBUTE_TYPE.TSK_EMAIL_CC.getTypeID() || attributeTypeID == ATTRIBUTE_TYPE.TSK_HEADERS.getTypeID()) {
    } else if (attributeTypeID == ATTRIBUTE_TYPE.TSK_EMAIL_CONTENT_PLAIN.getTypeID()) {
        String value = attribute.getDisplayString();
        if (value.length() > 160) {
            value = value.substring(0, 160) + "...";
        }
        map.put(attribute.getAttributeType().getDisplayName(), value);
    } else if (attribute.getAttributeType().getValueType() == BlackboardAttribute.TSK_BLACKBOARD_ATTRIBUTE_VALUE_TYPE.DATETIME) {
        map.put(attribute.getAttributeType().getDisplayName(), ContentUtils.getStringTime(attribute.getValueLong(), associated));
    } else {
        map.put(attribute.getAttributeType().getDisplayName(), attribute.getDisplayString());
    }
}

