void writeKeywordHits(TableReportModule tableModule,String comment,HashSet<String> tagNamesFilter){
    String orderByClause;
    Case openCase;
    try {
        openCase = Case.getCurrentCaseThrows();
    } catch (NoCurrentCaseException ex) {
        errorList.add(Bundle.ReportGenerator_errList_noOpenCase());
        logger.log(Level.SEVERE, "Exception while getting open case: ", ex);
        return;
    }
    String tagIDList = "";
    if (!tagNamesFilter.isEmpty()) {
        try {
            Map<String, TagName> tagNamesMap = Case.getCurrentCaseThrows().getServices().getTagsManager().getDisplayNamesToTagNamesMap();
            for (String tagDisplayName : tagNamesFilter) {
                if (tagNamesMap.containsKey(tagDisplayName)) {
                    if (!tagIDList.isEmpty()) {
                        tagIDList += ",";
                    }
                    tagIDList += tagNamesMap.get(tagDisplayName).getId();
                } else {
                    if (tagDisplayName.endsWith(getNotableTagLabel())) {
                        String editedDisplayName = tagDisplayName.substring(0, tagDisplayName.length() - getNotableTagLabel().length());
                        if (tagNamesMap.containsKey(editedDisplayName)) {
                            if (!tagIDList.isEmpty()) {
                                tagIDList += ",";
                            }
                            tagIDList += tagNamesMap.get(editedDisplayName).getId();
                        }
                    }
                }
            }
        } catch (NoCurrentCaseException | TskCoreException ex) {
            logger.log(Level.SEVERE, "Exception while getting tag info - proceeding without tag filter: ", ex);
            tagIDList = "";
        }
    }
    // NON-NLS
    String adHocCountQuery = "SELECT COUNT(*) FROM " + "(SELECT art.artifact_id FROM blackboard_artifacts AS art, blackboard_attributes AS att1 ";
    if (!tagIDList.isEmpty()) {
        adHocCountQuery += ", blackboard_artifact_tags as tag ";
    }
    adHocCountQuery += "WHERE (att1.artifact_id = art.artifact_id) AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") ";
    if (!tagIDList.isEmpty()) {
        adHocCountQuery += " AND (art.artifact_id = tag.artifact_id) AND (tag.tag_name_id IN (" + tagIDList + ")) ";
    }
    adHocCountQuery += "EXCEPT " + "SELECT art.artifact_id FROM blackboard_artifacts AS art, blackboard_attributes AS att1 WHERE (att1.artifact_id = art.artifact_id) AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") AND (att1.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + ")) AS adHocHits";
    int adHocCount = 0;
    try (SleuthkitCase.CaseDbQuery dbQuery = openCase.getSleuthkitCase().executeQuery(adHocCountQuery)) {
        ResultSet adHocCountResultSet = dbQuery.getResultSet();
        if (adHocCountResultSet.next()) {
            adHocCount = adHocCountResultSet.getInt(1);
        } else {
            throw new TskCoreException("Error counting ad hoc keywords");
        }
    } catch (TskCoreException | SQLException ex) {
        errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedQueryKWLists"));
        logger.log(Level.SEVERE, "Failed to count ad hoc searches with query " + adHocCountQuery, ex);
        return;
    }
    if (openCase.getCaseType() == Case.CaseType.MULTI_USER_CASE) {
        orderByClause = "ORDER BY convert_to(list, 'SQL_ASCII') ASC NULLS FIRST";
    } else {
        orderByClause = "ORDER BY list ASC";
    }
    String keywordListQuery = "SELECT att.value_text AS list " + "FROM blackboard_attributes AS att, blackboard_artifacts AS art ";
    if (!tagIDList.isEmpty()) {
        keywordListQuery += ", blackboard_artifact_tags as tag ";
    }
    keywordListQuery += "WHERE att.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + " " + "AND art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + " " + "AND att.artifact_id = art.artifact_id ";
    if (!tagIDList.isEmpty()) {
        keywordListQuery += "AND (art.artifact_id = tag.artifact_id) " + "AND (tag.tag_name_id IN (" + tagIDList + ")) ";
    }
    if (adHocCount > 0) {
        keywordListQuery += " UNION SELECT \'\' AS list ";
    }
    keywordListQuery = "SELECT * FROM ( " + keywordListQuery + " ) kwListNames ";
    keywordListQuery += "GROUP BY list " + orderByClause;
    try (SleuthkitCase.CaseDbQuery dbQuery = openCase.getSleuthkitCase().executeQuery(keywordListQuery)) {
        ResultSet listsRs = dbQuery.getResultSet();
        List<String> lists = new ArrayList();
        while (listsRs.next()) {
            String list = listsRs.getString("list");
            if (list.isEmpty()) {
                list = NbBundle.getMessage(this.getClass(), "ReportGenerator.writeKwHits.userSrchs");
            }
            lists.add(list);
        }
        tableModule.startDataType(BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getDisplayName(), comment);
        tableModule.addSetIndex(lists);
        progressPanel.updateStatusLabel(NbBundle.getMessage(this.getClass(), "ReportGenerator.progress.processing", BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getDisplayName()));
    } catch (TskCoreException | SQLException ex) {
        errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedQueryKWLists"));
        logger.log(Level.SEVERE, "Failed to query keyword lists with query " + keywordListQuery, ex);
        return;
    }
    if (openCase.getCaseType() == Case.CaseType.MULTI_USER_CASE) {
        orderByClause = "ORDER BY convert_to(list, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(keyword, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(parent_path, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(name, 'SQL_ASCII') ASC NULLS FIRST, " + "convert_to(preview, 'SQL_ASCII') ASC NULLS FIRST";
    } else {
        orderByClause = "ORDER BY list ASC, keyword ASC, parent_path ASC, name ASC, preview ASC";
    }
    String keywordListsQuery = "SELECT art.artifact_id AS artifact_id, art.obj_id AS obj_id, att1.value_text AS keyword, att2.value_text AS preview, att3.value_text AS list, f.name AS name, f.parent_path AS parent_path " + "FROM blackboard_artifacts AS art, blackboard_attributes AS att1, blackboard_attributes AS att2, blackboard_attributes AS att3, tsk_files AS f " + "WHERE (att1.artifact_id = art.artifact_id) " + "AND (att2.artifact_id = art.artifact_id) " + "AND (att3.artifact_id = art.artifact_id) " + "AND (f.obj_id = art.obj_id) " + "AND (att1.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD.getTypeID() + ") " + "AND (att2.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD_PREVIEW.getTypeID() + ") " + "AND (att3.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + ") " + "AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") ";
    // NON-NLS
    String keywordAdHocQuery = "SELECT art.artifact_id AS artifact_id, art.obj_id AS obj_id, att1.value_text AS keyword, att2.value_text AS preview, \'\' AS list, f.name AS name, f.parent_path AS parent_path " + "FROM blackboard_artifacts AS art, blackboard_attributes AS att1, blackboard_attributes AS att2, tsk_files AS f " + "WHERE " + " (art.artifact_id IN (SELECT art.artifact_id FROM blackboard_artifacts AS art, blackboard_attributes AS att1 WHERE (att1.artifact_id = art.artifact_id) AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") " + "EXCEPT " + "SELECT art.artifact_id FROM blackboard_artifacts AS art, blackboard_attributes AS att1 WHERE (att1.artifact_id = art.artifact_id) AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") AND (att1.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME.getTypeID() + "))) " + "AND (att1.artifact_id = art.artifact_id) " + "AND (att2.artifact_id = art.artifact_id) " + "AND (f.obj_id = art.obj_id) " + "AND (att1.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD.getTypeID() + ") " + "AND (att2.attribute_type_id = " + BlackboardAttribute.ATTRIBUTE_TYPE.TSK_KEYWORD_PREVIEW.getTypeID() + ") " + "AND (art.artifact_type_id = " + BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getTypeID() + ") ";
    String keywordsQuery = "SELECT * FROM ( " + keywordListsQuery + " UNION " + keywordAdHocQuery + " ) kwHits " + orderByClause;
    try (SleuthkitCase.CaseDbQuery dbQuery = openCase.getSleuthkitCase().executeQuery(keywordsQuery)) {
        ResultSet resultSet = dbQuery.getResultSet();
        String currentKeyword = "";
        String currentList = "";
        while (resultSet.next()) {
            if (progressPanel.getStatus() == ReportProgressPanel.ReportStatus.CANCELED) {
                break;
            }
            HashSet<String> uniqueTagNames = getUniqueTagNames(resultSet.getLong("artifact_id"));
            if (failsTagFilter(uniqueTagNames, tagNamesFilter)) {
                continue;
            }
            String tagsList = makeCommaSeparatedList(uniqueTagNames);
            Long objId = resultSet.getLong("obj_id");
            String keyword = resultSet.getString("keyword");
            String preview = resultSet.getString("preview");
            String list = resultSet.getString("list");
            String uniquePath = "";
            try {
                AbstractFile f = openCase.getSleuthkitCase().getAbstractFileById(objId);
                if (f != null) {
                    uniquePath = openCase.getSleuthkitCase().getAbstractFileById(objId).getUniquePath();
                }
            } catch (TskCoreException ex) {
                errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedGetAbstractFileByID"));
                logger.log(Level.WARNING, "Failed to get Abstract File by ID.", ex);
            }
            if ((!list.equals(currentList) && !list.isEmpty()) || (list.isEmpty() && !currentList.equals(NbBundle.getMessage(this.getClass(), "ReportGenerator.writeKwHits.userSrchs")))) {
                if (!currentList.isEmpty()) {
                    tableModule.endTable();
                    tableModule.endSet();
                }
                currentList = list.isEmpty() ? NbBundle.getMessage(this.getClass(), "ReportGenerator.writeKwHits.userSrchs") : list;
                currentKeyword = "";
                tableModule.startSet(currentList);
                progressPanel.updateStatusLabel(NbBundle.getMessage(this.getClass(), "ReportGenerator.progress.processingList", BlackboardArtifact.ARTIFACT_TYPE.TSK_KEYWORD_HIT.getDisplayName(), currentList));
            }
            if (!keyword.equals(currentKeyword)) {
                if (!currentKeyword.equals("")) {
                    tableModule.endTable();
                }
                currentKeyword = keyword;
                tableModule.addSetElement(currentKeyword);
                List<String> columnHeaderNames = new ArrayList();
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.preview"));
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.srcFile"));
                columnHeaderNames.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.artTableColHdr.tags"));
                tableModule.startTable(columnHeaderNames);
            }
            tableModule.addRow(Arrays.asList(new String[] { preview, uniquePath, tagsList }));
        }
        if (!currentKeyword.isEmpty()) {
            tableModule.endTable();
        }
        progressPanel.increment();
        tableModule.endDataType();
    } catch (TskCoreException | SQLException ex) {
        errorList.add(NbBundle.getMessage(this.getClass(), "ReportGenerator.errList.failedQueryKWs"));
        logger.log(Level.SEVERE, "Failed to query keywords with query " + keywordsQuery, ex);
    }
}

