double calculateEntropy(Content content){
    InputStream in = null;
    BufferedInputStream bin = null;
    try {
        in = new ReadContentInputStream(content);
        bin = new BufferedInputStream(in);
        int[] byteOccurences = new int[BYTE_OCCURENCES_BUFFER_SIZE];
        int readByte;
        while ((readByte = bin.read()) != -1) {
            byteOccurences[readByte]++;
        }
        long dataLength = content.getSize() - 1;
        double entropyAccumulator = 0;
        for (int i = 0; i < BYTE_OCCURENCES_BUFFER_SIZE; i++) {
            if (byteOccurences[i] > 0) {
                double byteProbability = (double) byteOccurences[i] / (double) dataLength;
                entropyAccumulator += (byteProbability * Math.log(byteProbability) * ONE_OVER_LOG2);
            }
        }
        return -entropyAccumulator;
    } finally {
        if (in != null) {
            in.close();
        }
        if (bin != null) {
            bin.close();
        }
    }
}

boolean isGroupSeenByExaminer(GroupKey<?> groupKey,long examinerId){
    class GroupSeenQueryResultProcessor extends CompletableFuture<Boolean> implements CaseDbAccessQueryCallback {
        @Override
        public void process(ResultSet resultSet) {
            try {
                if (resultSet != null) {
                    while (resultSet.next()) {
                        complete(resultSet.getInt("count") > 0);
                        return;
                    }
                }
            } catch (SQLException ex) {
                logger.log(Level.SEVERE, "Failed to get group seen", ex);
            }
        }
    }
    GroupSeenQueryResultProcessor queryResultProcessor = new GroupSeenQueryResultProcessor();
    try {
        String groupSeenQueryStmt = "COUNT(*) as count FROM " + GROUPS_SEEN_TABLENAME + " WHERE seen = 1 " + " AND group_id in ( " + getGroupIdQuery(groupKey) + ")" + (examinerId > 0 ? " AND examiner_id = " + examinerId : "");
        tskCase.getCaseDbAccessManager().select(groupSeenQueryStmt, queryResultProcessor);
        return queryResultProcessor.get();
    } catch (ExecutionException | InterruptedException | TskCoreException ex) {
        String msg = String.format("Failed to get is group seen for group key %s", groupKey.getValueDisplayName());
        logger.log(Level.WARNING, msg, ex);
    }
    return false;
}

