void commitOrRevertAddImageProcess(Case currentCase,List<String> errorMessages,List<Content> newDataSources){
    synchronized (tskAddImageProcessLock) {
        if (tskAddImageProcessStopped || criticalErrorOccurred) {
            try {
                tskAddImageProcess.revert();
            } catch (TskCoreException ex) {
                logger.log(Level.SEVERE, String.format("Error reverting adding image %s to the case database", imagePath), ex);
                errorMessages.add(ex.getMessage());
                criticalErrorOccurred = true;
            }
        } else {
            try {
                long imageId = tskAddImageProcess.commit();
                if (imageId != 0) {
                    Image newImage = currentCase.getSleuthkitCase().getImageById(imageId);
                    String verificationError = newImage.verifyImageSize();
                    if (!verificationError.isEmpty()) {
                        errorMessages.add(verificationError);
                    }
                    if (imageWriterSettings != null) {
                        ImageWriterService.createImageWriter(imageId, imageWriterSettings);
                    }
                    newDataSources.add(newImage);
                } else {
                    String errorMessage = String.format("Error commiting adding image %s to the case database, no object id returned", imagePath);
                    logger.log(Level.SEVERE, errorMessage);
                    errorMessages.add(errorMessage);
                    criticalErrorOccurred = true;
                }
            } catch (TskCoreException ex) {
                logger.log(Level.SEVERE, String.format("Error committing adding image %s to the case database", imagePath), ex);
                errorMessages.add(ex.getMessage());
                criticalErrorOccurred = true;
            }
        }
    }
}

