void stop(){
    try {
        closeCore();
    } catch (KeywordSearchModuleException e) {
        logger.log(Level.WARNING, "Failed to close core: ", e);
    }
    try {
        logger.log(Level.INFO, "Stopping Solr server from: {0}", solrFolder.getAbsolutePath());
        Process process = runSolrCommand(new ArrayList(Arrays.asList("--stop")));
        logger.log(Level.INFO, "Waiting for Solr server to stop");
        process.waitFor();
        if (curSolrProcess != null) {
            curSolrProcess.destroy();
            curSolrProcess = null;
        }
    } catch (IOException | InterruptedException ex) {
        logger.log(Level.WARNING, "Error while attempting to stop Solr server", ex);
    } finally {
        try {
            if (errorRedirectThread != null) {
                errorRedirectThread.stopRun();
                errorRedirectThread = null;
            }
        } finally {
            killSolr();
        }
        logger.log(Level.INFO, "Finished stopping Solr server");
    }
}

