boolean isZipBombArchiveItemCheck(AbstractFile archiveFile,ISevenZipInArchive inArchive,int inArchiveItemIndex,ConcurrentHashMap<Long, Archive> depthMap,String escapedFilePath){
    if (archiveFile.isMetaFlagSet(TskData.TSK_FS_META_FLAG_ENUM.UNALLOC)) {
        return false;
    }
    try {
        final Long archiveItemSize = (Long) inArchive.getProperty(inArchiveItemIndex, PropID.SIZE);
        if (archiveItemSize == null || archiveItemSize < MIN_COMPRESSION_RATIO_SIZE) {
            return false;
        }
        final Long archiveItemPackedSize = (Long) inArchive.getProperty(inArchiveItemIndex, PropID.PACKED_SIZE);
        if (archiveItemPackedSize == null || archiveItemPackedSize <= 0) {
            // NON-NLS
            logger.log(Level.WARNING, "Cannot getting compression ratio, cannot detect if zipbomb: {0}, item: {1}", new Object[] { archiveFile.getName(), (String) inArchive.getProperty(inArchiveItemIndex, PropID.PATH) });
            return false;
        }
        int cRatio = (int) (archiveItemSize / archiveItemPackedSize);
        if (cRatio >= MAX_COMPRESSION_RATIO) {
            Archive rootArchive = depthMap.get(depthMap.get(archiveFile.getId()).getRootArchiveId());
            String details = NbBundle.getMessage(SevenZipExtractor.class, "EmbeddedFileExtractorIngestModule.ArchiveExtractor.isZipBombCheck.warnDetails", cRatio, FileUtil.escapeFileName(getArchiveFilePath(rootArchive.getArchiveFile())));
            flagRootArchiveAsZipBomb(rootArchive, archiveFile, details, escapedFilePath);
            return true;
        } else {
            return false;
        }
    } catch (SevenZipException ex) {
        logger.log(Level.WARNING, "Error getting archive item size and cannot detect if zipbomb. ", ex);
        return false;
    }
}

